import{_ as a}from"./chunks/highload_gripper_example.99YFJ085.js";import{_ as r,a as t}from"./chunks/qgc_gripper_output_setup.Cu2P72Ow.js";import{_ as e,c as o,o as p,ab as i}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"PWM Сервопривод","description":"","frontmatter":{},"headers":[],"relativePath":"uk/peripherals/gripper_servo.md","filePath":"uk/peripherals/gripper_servo.md"}'),l={name:"uk/peripherals/gripper_servo.md"},s=i('<h1 id="pwm-сервопривод" tabindex="-1">PWM Сервопривод <a class="header-anchor" href="#pwm-сервопривод" aria-label="Permalink to &quot;PWM Сервопривод&quot;">​</a></h1><p>Цей розділ пояснює, як підключити та налаштувати <a href="./../peripherals/gripper.html">захоплювач</a>, який керується виходом ШШП контролера польоту (серводією).</p><p><img src="'+a+'" alt="R4-EM-R22-16: High-load gripper example"></p><h2 id="підтримувані-захвати" tabindex="-1">Підтримувані захвати <a class="header-anchor" href="#підтримувані-захвати" aria-label="Permalink to &quot;Підтримувані захвати&quot;">​</a></h2><p>Наступні сервоприводи, підключені за допомогою ШІМ, були протестовані з PX4:</p><ul><li><a href="https://southco.com/en_any_int/r4-em-r22-161" target="_blank" rel="noreferrer">R4-EM-R22-161 : електронний замок з витиском для зачинення</a>.</li></ul><h2 id="підключення-маніпулятора-з-керуванням-шім" tabindex="-1">Підключення маніпулятора з керуванням ШІМ <a class="header-anchor" href="#підключення-маніпулятора-з-керуванням-шім" aria-label="Permalink to &quot;Підключення маніпулятора з керуванням ШІМ&quot;">​</a></h2><p>Кабель ШІМ складається з трьох ліній: живлення, земля та сигнал. Типовий роз&#39;єм показаний на зображенні нижче:</p><p><img src="'+r+'" alt="PWM Cable"></p><p>На зображенні вище кольори проводів мають наступні значення:</p><table><thead><tr><th>Колір проводу</th><th>Ціль</th></tr></thead><tbody><tr><td>Brown</td><td>Ground</td></tr><tr><td>Red</td><td>Power</td></tr><tr><td>Yellow</td><td>Сигнал PWM</td></tr></tbody></table><p>Вам потрібно буде підключити їх до відповідного входу PWM контролера польоту.</p><h3 id="перевірка-сумісності" tabindex="-1">Перевірка сумісності <a class="header-anchor" href="#перевірка-сумісності" aria-label="Permalink to &quot;Перевірка сумісності&quot;">​</a></h3><p>Перед підключенням кабелю перевірте наступні вимоги:</p><ul><li><strong>Рівень напруги сигнальної лінії</strong>: Перевірте технічні характеристики механізму захвату, щоб знайти рівень напруги сигнальної лінії. І переконайтеся, що це сумісно з рівнем напруги контактів вашого контролера польоту.</li><li><strong>Вимоги до живлення захвата</strong>: Перевірте технічний опис механізму, щоб дізнатися вимоги до напруги живлення лінії. Залежно від цього, захоплювач може бути підключений безпосередньо до <a href="./../power_module/">модуля живлення</a> або підключений до лінії 5V. Також можна використовувати спеціалізований регулятор напруги для виведення будь-якої іншої необхідної напруги.</li></ul><h2 id="конфігурація-px4" tabindex="-1">Конфігурація PX4 <a class="header-anchor" href="#конфігурація-px4" aria-label="Permalink to &quot;Конфігурація PX4&quot;">​</a></h2><p>Інструкції з налаштування можна знайти в: <a href="./../peripherals/gripper.html#px4-configuration">Gripper &gt; PX4 Configuration</a> документації.</p><p>Зокрема, зверніть увагу, що серводатчик повинен бути відображений на вивід, як показано нижче.</p><h3 id="відображення-активатора" tabindex="-1">Відображення активатора <a class="header-anchor" href="#відображення-активатора" aria-label="Permalink to &quot;Відображення активатора&quot;">​</a></h3><p>PWM сервоприводи, та інші периферійні пристрої, які підключені безпосередньо до виходів PWM, повинні бути відображені на конкретні виходи під час <a href="./../config/actuators.html#actuator-outputs">Конфігурації приводів</a>.</p><p>Це виконується шляхом призначення функції <code>Gripper</code> для вихідного порту, де підйомник підключений. Наприклад, на зображенні нижче призначається <code>Gripper</code> для виходу PWM AUX5.</p><p><img src="'+t+'" alt="Gripper output mapping"></p><p>Вам також потрібно встановити правильну частоту ШІМ для вихідного порту захоплювача (для комерційних сервоприводів / захоплювачів це зазвичай 50 Гц).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Неправильна настройка частоти може пошкодити захват.</p></div><p>Повзунки в розділі <a href="./../config/actuators.html#actuator-testing">Тестування приводів</a> екрану конфігурації можуть бути використані для перевірки того, що відповідний вивід рухається, коли ви переміщуєте повзунок. Мінімальні та максимальні значення ШІМ повинні бути встановлені так, щоб сервопривід був повністю закритий у відключеному положенні і повністю відкритий в максимальному положенні слайдера.</p>',25),h=[s];function d(n,c,_,u,m,f){return p(),o("div",null,h)}const x=e(l,[["render",d]]);export{k as __pageData,x as default};
