import{_ as l,a as i}from"./chunks/pm03d_contents.BF2cHk11.js";import{_ as a}from"./chunks/pm02d_pinout.7bTPG-dY.js";import{_ as o,c as r,o as t,ab as e}from"./chunks/framework.CUflZczI.js";const P=JSON.parse('{"title":"Модуль живлення Holybro PM03D","description":"","frontmatter":{},"headers":[],"relativePath":"uk/power_module/holybro_pm03d.md","filePath":"uk/power_module/holybro_pm03d.md"}'),n={name:"uk/power_module/holybro_pm03d.md"},s=e('<h1 id="модуль-живлення-holybro-pm03d" tabindex="-1">Модуль живлення Holybro PM03D <a class="header-anchor" href="#модуль-живлення-holybro-pm03d" aria-label="Permalink to &quot;Модуль живлення Holybro PM03D&quot;">​</a></h1><p>Плата живлення PM03D виступає як блок живлення (PM) та розподільча плата живлення (PDB). Крім надання регульованого живлення Pixhawk v5X та ESC, вона надсилає інформацію автопілоту про напругу батареї та поточний струм, який подається на керуючий пристрій польоту та двигуни.</p><p>Плата живлення підключається за допомогою протоколу I2C. Вона призначена для керуючих пристроїв польоту на основі відкритого стандарту Pixhawk FMUv5X та FMUv6X, включаючи <a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X</a>.</p><div class="info custom-block"><p class="custom-block-title">PM <strong>НЕ</strong>сумісний із контролерами польоту, яким потрібен аналоговий модуль живлення, зокрема: <a href="./../flight_controller/pixhawk4.html">Pixhawk 4</a>, <a href="./../flight_controller/durandal.html">Durandal</a>, <a href="./../flight_controller/holybro_pix32_v5.html">Pix32 v5</a> тощо.</p></div><p><img src="'+l+'" alt="Pixhawk5x Upright Image"></p><h2 id="функціі" tabindex="-1">Функції <a class="header-anchor" href="#функціі" aria-label="Permalink to &quot;Функції&quot;">​</a></h2><ul><li>Підключи &amp; використовуй, жодна додаткова настройка не потрібна для QGroundcontrol &amp; Mission Planner</li><li>Роз&#39;єми XT-30 та паяльні площини для простого підключення моторів та ESC</li><li>Вбудовані стабілізатори напруги: два 5.2V &amp; один на вибір 8V/12V</li><li>Порти 5V/A для живлення супутнього комп&#39;ютера або периферійного пристрою</li><li>На вибір 8V або 12V трирядний рядок контактів для живлення периферійного пристрою</li></ul><h2 id="специфікаціі" tabindex="-1">Специфікації <a class="header-anchor" href="#специфікаціі" aria-label="Permalink to &quot;Специфікації&quot;">​</a></h2><ul><li><strong>Максимальна вхідна напруга</strong>: 6S заряд акумулятора</li><li><strong>Оцінений струм</strong>: 60A</li><li><strong>Максимальний струм:</strong>: 120A (&lt;60 сек)</li><li><strong>Максимальне вимірювання поточного струму</strong>: 164A</li><li><strong>Протокол зв&#39;язку</strong>: I2C</li><li><strong>Розмір</strong>: 84 x 78 x 12 мм (без дротів)</li><li><strong>Монтування</strong>: 45 х 45 мм</li><li><strong>Вага</strong>: 59g</li><li><strong>Підключення</strong>:</li><li>ХТ-60 для акумулятора</li><li>XT-30 для мотора &amp; периферійний пристрій (напруга акумулятора)</li><li>Паяльні вклади в кожному кутку (напруга акумулятора)</li><li>CLIK-Mate 2.0мм для контролю за польотами (5.2V/3A standalone BEC)</li><li>JST GH 4pin (5.2V/3A, BEC shared with 5.2V потрійний рядок шпильковий заголовок)</li><li>2x Трьохрядний шплинтовий рядок (5.2V/3A, BEC спільний з JST GH 4pin)</li><li>2x Трьохрядний шплинтовий заголовок (8V або 12V вибирається переміщенням стрижня заголовка, 3A)</li></ul><h2 id="вміст-набору" tabindex="-1">Вміст набору <a class="header-anchor" href="#вміст-набору" aria-label="Permalink to &quot;Вміст набору&quot;">​</a></h2><ul><li>1x Плата PM06</li><li>1x 80 мм кабель з роз&#39;ємом XT60 (попередньо припаяний)</li><li>1x Електролітична ємність: 220uF 63V (передпаяна)</li><li>1x 2.0мм інтервал CLIK-Mate 6pin кабель (Для живлення контролера польоту)</li><li>4pin JST GH в USB тип C</li><li>4pin JST GH до бочкового штекера (2.1*5.5мм)</li><li>4pin JST GH до бочкового штекера (2.5*5.5мм)</li><li>Кабель Дюпон 4-шпильковий (2шт)</li><li>Нейлонові стійки &amp; гайки</li></ul><img src="'+i+'" width="650px" title="Pixhawk5x Upright Image"><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p><a href="https://holybro.com/products/pm03d-power-module" target="_blank" rel="noreferrer">Замовляйте на веб-сайті Holybro Store</a></p><h2 id="проводка-з-єднання" tabindex="-1">Проводка/З&#39;єднання <a class="header-anchor" href="#проводка-з-єднання" aria-label="Permalink to &quot;Проводка/З&#39;єднання&quot;">​</a></h2><p><img src="'+a+'" alt="pinout"></p><p>Додаткову інформацію про проводку та підключення можна знайти в: <a href="./../assembly/quick_start_pixhawk5x.html">Holybro Pixhawk 5x Wiring Quick Start</a>.</p>',17),h=[s];function p(d,m,_,c,u,g){return t(),r("div",null,h)}const k=o(n,[["render",p]]);export{P as __pageData,k as default};
