import{_ as l,c as t,o as r,ab as e}from"./chunks/framework.CUflZczI.js";const f=JSON.parse('{"title":"Реліз 1.12","description":"","frontmatter":{},"headers":[],"relativePath":"uk/releases/1.12.md","filePath":"uk/releases/1.12.md"}'),a={name:"uk/releases/1.12.md"},i=e(`<h1 id="реліз-1-12" tabindex="-1">Реліз 1.12 <a class="header-anchor" href="#реліз-1-12" aria-label="Permalink to &quot;Реліз 1.12&quot;">​</a></h1><ul><li><a href="#release-1-12">Реліз 1.12</a><ul><li><a href="#pre-releases">Попередній реліз</a></li><li><a href="#changes">Зміни</a><ul><li><a href="#common">Загальні</a></li><li><a href="#sensors">Сенсори</a></li><li><a href="#hardware">Апаратне забезпечення(Hardware)</a></li><li><a href="#mavlink">MAVLink</a></li><li><a href="#commander">Командер</a></li><li><a href="#multicopter">Мультикоптер</a></li><li><a href="#vtol">VTOL</a></li><li><a href="#control">Управління</a></li><li><a href="#gps">GPS</a></li><li><a href="#nuttx">NuttX</a></li><li><a href="#uavcan">UAVCAN</a></li></ul></li></ul></li></ul><h2 id="попередніи-випуск" tabindex="-1">Попередній випуск <a class="header-anchor" href="#попередніи-випуск" aria-label="Permalink to &quot;Попередній випуск&quot;">​</a></h2><ul><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta4" target="_blank" rel="noreferrer">Beta 4</a></li><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta3" target="_blank" rel="noreferrer">Beta 3</a></li><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta2" target="_blank" rel="noreferrer">Beta 2</a></li><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta1" target="_blank" rel="noreferrer">Beta 1</a></li></ul><h2 id="зміни" tabindex="-1">Зміни <a class="header-anchor" href="#зміни" aria-label="Permalink to &quot;Зміни&quot;">​</a></h2><h3 id="загальні" tabindex="-1">Загальні <a class="header-anchor" href="#загальні" aria-label="Permalink to &quot;Загальні&quot;">​</a></h3><ul><li><strong>RTL Тригер, що ґрунтується на залишковому польотному діапазоні (<a href="https://github.com/PX4/PX4-Autopilot/pull/16399" target="_blank" rel="noreferrer">PR#16399</a>)</strong><ul><li>Обчислює час до дому, у режимі RTL, враховуючи швидкість руху автомобіля, швидкість вітру та відстань/напрямок до пункту призначення</li></ul></li><li><strong>Заздалегідь порушення геозаборони (<a href="https://github.com/PX4/PX4-Autopilot/pull/16400" target="_blank" rel="noreferrer">PR#16400</a>)</strong><ul><li>Спрацьовує порушення, якщо <em>передбачається</em>, що поточна траєкторія призведе до порушення, дозволяючи перенаправити транспортний засіб до безпечного місця утримання.</li></ul></li><li><strong>Сценарії та конструкції повітряного корпусу</strong><ul><li>Синтаксис для встановлення значень за замовчуванням було змінено, і власні сценарії потребують оновлення</li><li>Див. <a href="https://github.com/PX4/PX4-Autopilot/pull/16796/files#diff-dcf2f5536f47f260e5e0ff3b3fd22eaef6b6c510126463d70affa0eb7bd4d3ddL20" target="_blank" rel="noreferrer">PR#16796</a> для прикладу.</li></ul></li><li>Безпека (перемикач) за замовчуванням вимкнена (двигуни розброньовані, але сервоприводи / закрилки можуть рухатися)</li><li>Безпековий перемикач є защіпним: коли він вимкнутий, він залишиться вимкнутим</li><li>Засіб виявлення землі: Розширити виявлення землі для використання відстані до ґрунту, якщо датчик відстані присутній</li><li>Додана підтримка для IRC Ghost включаючи телеметрію</li></ul><h3 id="сенсори" tabindex="-1">Сенсори <a class="header-anchor" href="#сенсори" aria-label="Permalink to &quot;Сенсори&quot;">​</a></h3><ul><li>Калібрування магнітомера швидше і надійніше <ul><li>нові коефіцієнти калібрування для м&#39;якого заліза</li><li>автоматично визначати обертання зовнішніх датчиків</li></ul></li><li>Оптимізований датчик керування швидкістю потоку даних (мінімальна затримка від кінця до кінця внутрішньої петлі)</li></ul><h3 id="апаратне-забезпечення" tabindex="-1">Апаратне забезпечення <a class="header-anchor" href="#апаратне-забезпечення" aria-label="Permalink to &quot;Апаратне забезпечення&quot;">​</a></h3><p>Випуск включає новий апаратний засіб для наступних плат, периферійних пристроїв та аксесуарів:</p><ul><li>Pixhawk FMUv6U (Докладніше про цю специфікацію можна переглянути на <a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">сховищі GitHub Pixhawk</a>)</li><li>Pixhawk FMUv6X (Докладніше про ці специфікації можна прочитати на <a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">сховищі Pixhawk GitHub</a>)</li><li>CUAV X7 / X7Pro (<a href="http://www.cuav.net/en/x7en/" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>]</li><li>CUAV Нора (<a href="http://www.cuav.net/en/nora/" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>)</li><li>CUAV CAN GPS (<a href="http://www.cuav.net/en/neo-3-2/" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>)</li><li>SP Racing H7 Extreme (<a href="http://seriouslypro.com/spracingh7extreme" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>)</li><li>Bitcraze Crazyflie v2.1 (<a href="https://www.bitcraze.io/products/crazyflie-2-1/" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>)</li><li>ARK CAN Flow (<a href="https://arkelectron.com/product/ark-flow/" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>)</li><li>mRo Ctrl Zero H7 (Експериментальний) (<a href="https://store.mrobotics.io/mRo-Control-Zero-F7-p/mro-ctrl-zero-f7.htm" target="_blank" rel="noreferrer">Дізнайтеся більше про цей продукт на сайті виробника</a>)</li></ul><p>Вони видалені:</p><ul><li>Вилучення з припиненням виробництва Intel AeroFC</li></ul><h3 id="mavlink" tabindex="-1">MAVLink <a class="header-anchor" href="#mavlink" aria-label="Permalink to &quot;MAVLink&quot;">​</a></h3><ul><li><strong>Конфігурація MAVLink Ethernet (<a href="https://github.com/PX4/PX4-Autopilot/pull/14460" target="_blank" rel="noreferrer">PR#14460</a>)</strong><ul><li>Налаштування каналу Ethernet MAVLink, такі як UDP-порт, віддалений порт та режим мовлення тепер можуть бути змінені динамічно за допомогою параметрів.</li></ul></li><li><strong>Підтримка запиту на отримання <code>COMPONENT_INFORMATION</code> (<a href="https://github.com/PX4/PX4-Autopilot/pull/16039" target="_blank" rel="noreferrer">PR#16039</a>)</strong><ul><li>Метадані параметра тепер автоматично синхронізуються в поєднанні з щоденним QGC.</li><li>Це нове повідомлення дозволяє будь-якій системі MAVLink запитувати різноманітну ієрархічну інформацію від автопілота, тобто розуміти, які команди підтримуються в місіях або отримувати метадані параметрів. Це повідомлення було введено в основному для того, щоб допомогти GCS краще розуміти автопілоти (RFC: <a href="https://github.com/mavlink/mavlink/issues/1339" target="_blank" rel="noreferrer">mavlink#1339</a>)</li></ul></li></ul><h3 id="головнокомандувач" tabindex="-1">Головнокомандувач <a class="header-anchor" href="#головнокомандувач" aria-label="Permalink to &quot;Головнокомандувач&quot;">​</a></h3><ul><li><strong>Командер: використовуйте прапорці режиму керування та очищення озброєння/відбронювання (<a href="https://github.com/PX4/PX4-Autopilot/pull/16266" target="_blank" rel="noreferrer">PR#16266</a>)</strong><ul><li>Узгодьте розкидані вимоги щодо озброєння в arm_disarm(), та зберігайте останній стан <code>vehicle_control_mode</code> у командирі</li></ul></li><li><strong>Командер: Розділіть обробку установки керування вручну (<a href="https://github.com/PX4/PX4-Autopilot/pull/16878" target="_blank" rel="noreferrer">PR#16878</a>)</strong><ul><li>Додає новий клас <code>ManualControl</code> для обробки <code>manual_control_setpoint</code> та керує втратою RC, перевизначенням RC та активацією/дезактивацією RC</li></ul></li></ul><h3 id="мультикоптер" tabindex="-1">Мультикоптер <a class="header-anchor" href="#мультикоптер" aria-label="Permalink to &quot;Мультикоптер&quot;">​</a></h3><ul><li><p><strong>Більш інтуїтивне відчуття палиці в режимі Position</strong></p><ul><li>Горизонтальний вхід палиці відображений на точках прискорення замість встановлених точок швидкості</li><li>Видаляє неочікувані зміни нахилу при досягненні швидкості руху</li><li>Інтуїтивне переміщення, наприклад, при посадці</li><li>Opt out possible using <a href="./../advanced_config/parameter_reference.html#MPC_POS_MODE">MPC_POS_MODE</a></li><li>Розробка: <a href="https://github.com/PX4/PX4-Autopilot/pull/12072" target="_blank" rel="noreferrer">Перший спроба</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16052" target="_blank" rel="noreferrer">Вступ</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16320" target="_blank" rel="noreferrer">Покращення</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16786" target="_blank" rel="noreferrer">Виправлення помилки нульових коливань</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16791" target="_blank" rel="noreferrer">Виправлення помилки розблокування позиції</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/17078" target="_blank" rel="noreferrer">Виправлення невірно заданої точки</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/17437" target="_blank" rel="noreferrer">Виправлення високої швидкості перед зльотом</a></li></ul></li><li><p><strong>Виграш штовхання незалежних коефіцієнтів керування швидкістю</strong></p><ul><li>Параметри <code>MPC_{XY/Z}_VEL_{P/I/D}</code> були замінені на <code>MPC_{XY/Z}_VEL_{P/I/D}_ACC</code>, див.: <a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_P_ACC">MPC_XY_VEL_P_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_I_ACC">MPC_XY_VEL_I_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_D_ACC">MPC_XY_VEL_D_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_P_ACC">MPC_Z_VEL_P_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_I_ACC">MPC_Z_VEL_I_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_D_ACC">MPC_Z_VEL_D_ACC</a></li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Прибутки отримали нове значення</p><pre><code>- Шкала відхилення від швидкості у $м/с$ до виходу прискорення у $м/с^2$
- Існуючі виграші потрібно приблизно перескалювати множником: $гравітаційна \\_ стала / тяга \\_ струму$
- Автоматичний перехід параметра передбачає 50% тяги під час нависання: \`~10м/с² / 50% = 20 м/с²\`. Дивіться [питання](https://github.com/PX4/PX4-Autopilot/pull/14823#issuecomment-791357646)
</code></pre></div><ul><li><p>Розробка: <a href="https://github.com/PX4/PX4-Autopilot/pull/14749" target="_blank" rel="noreferrer">Введення логіки</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/14823" target="_blank" rel="noreferrer">Заміна параметра</a></p></li><li><p><strong>Покращити круглі повороти (<a href="https://github.com/PX4/PX4-Autopilot/pull/16376" target="_blank" rel="noreferrer">PR#16376</a>)</strong></p><ul><li>Створює більш округлений поворот при точках маршруту в місіях мультиротора (використовуючи логіку навігації у стилі L1 на кутах)</li><li>Дивіться <a href="./../flight_modes_fw/mission.html#rounded-turns-inter-waypoint-trajectory">Режим Місії &gt; Міжточкова Траєкторія</a> та <a href="./../flying/missions.html#setting-acceptance-turning-radius">Місія &gt; Встановлення Прийняття/Радіус Обертання</a></li></ul></li><li><p><strong>Видалення режиму польоту Rattitude (<a href="https://github.com/PX4/PX4-Autopilot/pull/17019" target="_blank" rel="noreferrer">PR#17019</a>)</strong></p><ul><li>Сповістіть нас, якщо ви хочете повернути його.</li></ul></li></ul><h3 id="vtol" tabindex="-1">VTOL <a class="header-anchor" href="#vtol" aria-label="Permalink to &quot;VTOL&quot;">​</a></h3><ul><li><strong>Покращення RTL (<a href="https://github.com/PX4/PX4-Autopilot/pull/16377" target="_blank" rel="noreferrer">PR#16377</a>)</strong><ul><li>Підвищує надійність RTL безпекових заходів, враховуючи багато крайніх випадків при спробі посадки, залежно від поточного режиму транспортного засобу (Багатокоптерний проти Фіксований крило)</li></ul></li><li>Значні вдосконалення ТЕКС фіксованого крила / VTOL</li></ul><h3 id="управління" tabindex="-1">Управління <a class="header-anchor" href="#управління" aria-label="Permalink to &quot;Управління&quot;">​</a></h3><ul><li><strong>Динамічний фільтр пазуру оновлено за допомогою Гіроскопічного FFT (<a href="https://github.com/PX4/PX4-Autopilot/pull/16385" target="_blank" rel="noreferrer">PR#16385</a>)</strong><ul><li>Додає динамічне фільтрування виѐмок до даних керування гіроскопом, що призводить до набагато плавнішого керування</li></ul></li><li><strong>Багатоступеневий EKF активовано за замовчуванням</strong> на платах stm32f7 та stm32h7</li></ul><h3 id="gps" tabindex="-1">GPS <a class="header-anchor" href="#gps" aria-label="Permalink to &quot;GPS&quot;">​</a></h3><ul><li>GPS-протокол тепер за замовчуванням має бути змінений u-blox для швидкого запуску, а <a href="./../advanced_config/parameter_reference.html#GPS_1_PROTOCOL">GPS_x_PROTOCOL</a> під час використання іншого GPS.</li></ul><h3 id="nuttx" tabindex="-1">NuttX <a class="header-anchor" href="#nuttx" aria-label="Permalink to &quot;NuttX&quot;">​</a></h3><p>Nuttx було оновлено з <a href="https://github.com/apache/incubator-nuttx/compare/nuttx-8.2..nuttx-10.0.1" target="_blank" rel="noreferrer">8.2+ до NuttX 10.10.0+</a> (@ <a href="https://github.com/apache/incubator-nuttx/commit/904a602c74dc08a100b5c2bd490807de19e73e10" target="_blank" rel="noreferrer">904a602c74dc08a100b5c2bd490807de19e73e10</a>)</p><ul><li><p><strong>Продуктивність SDCARD:</strong> Результат у покращенні продуктивності на цілях H7</p><ul><li>[<strong>BACKPORT</strong>] stm32:SDIO:Використовуйте тайм-аут шляху даних 250 мс, незалежно від частоти картки</li><li>[** BACKPORT **] stm32h7:SDMMC:Використовуйте таймаут шляху даних 250 мс, незалежно від частоти картки</li><li>[<strong>БЕКПОРТ</strong>] stm32f7:SDMMC:Використовуйте таймаут шляху даних 250 мс, незалежно від частоти картки</li><li>[<strong>BACKPORT</strong>] Виправлення гонки при умовах очікування в логіці драйвера SDMMC.</li><li>[<strong>BACKPORT</strong>] mmcsd:Застряг в 1-бітному режимі, Видалено CONFIG_ARCH_HAVE_SDIO_DELAYED_INVLDT</li></ul></li><li><p><strong>Стабільність Ethernet:</strong></p><ul><li>[<strong>Назадпорт</strong>] stm32x7:Ethernet Виправлені hardfaults, занадто великі фрейми</li><li>[<strong>ВІДШИВАННЯ</strong>] stm32:Ethernet Fix too big frames</li></ul></li><li><p><strong>Стабільність завантаження</strong> V5-V6X забезпечує запуск годинника LSE (RTC)</p><ul><li><p>[<strong>BACKPORT</strong>] stm32h7:lse виправлення тексту довідки Kconfig</p></li><li><p>[** BACKPORT **] stm32f7:lse Використання значень Kconfig безпосередньо</p></li><li><p>[** BACKPORT **] stm32h7:Додавання DBGMCU</p></li><li><p>[** BACKPORT **] stm32f7: Додати опцію автоматичного вибору LSE ЗДАТНІСТЬ</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:Додано опцію автоматичного вибору LSE CAPABILITY</p><div class="info custom-block"><p class="custom-block-title">INFO</p></div></li></ul></li></ul><p>Ця Ручка переключиться через вірні* значення від низького до високого. Щоб уникнути пошкодження кристалу. Ми хочемо використовувати найнижче значення , що запускає OSC. Див. додаток AN2867 *Функція враховуватиме версію кремнію і використовуватиме правильні кодові точки для досягнення впливу приводу. Див. Eratta ES0392 Rev 7 2.2.14 Вибір бітів для вибору можливості приводу LSE осцилятора обмінюється. :::</p><ul><li><p><strong>Зміни водія</strong></p><ul><li><p>[** BACKPORT **] драйвери/серійний порт: виправлення увімкнення переривань Rx для cdcacm</p></li><li><p>[** BACKPORT **] binnfmt:Виправлення повернення перед закриттям файлового дескриптора ELF</p></li><li><p>[<strong>BACKPORT</strong>] stm32f7:Дозволити повторне використання GPIO OTG_ID</p></li><li><p>[<strong>BACKPORT</strong>] stm32f7:SDMMC виправлення скидання do_gpio</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7: serial: використання dma tx semaphore як resource holder</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:SDMMC виправлення скидання do_gpio</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7: Serial Додавання RX та TX DMA</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:Дозволити повторне використання GPIO OTG_ID</p></li><li><p>[<strong>BACKPORT</strong>] Kinetis:kinetis:Заміна DMA</p></li><li><p>[<strong>BACKPORT</strong>] kinetis:Serial використання eDMA</p></li><li><p>[<strong>BACKPORT</strong>] kinetis:SPI використання eDMA</p></li><li><p>[<strong>BACKPORT</strong>] Kinetis:Serail Не потрібен DMA Poll</p></li><li><p>[<strong>BACKPORT</strong>] libc/stdio: Преалокація stdin, stdout та stderr для систем без консолей.</p></li></ul></li><li><p><strong>Виправлення FlexCan</strong></p><ul><li>[<strong>BACKPORT</strong>][flexcan] Виправлення стану скидання для CTRL1 register</li><li>[<strong>BACKPORT</strong>][flexcan] Виправлення TX drop #2792 та правильне налаштування таймінгу CAN на ненульові реєстри</li><li>[<strong>BACKPORT</strong>] FlexCAN Виправлення відміни процесу TX</li></ul></li><li><p><strong>Підтримка завантажувача CAN</strong></p><ul><li>[<strong>BACKPORT</strong>] s32k1xx:Підтримка ramfunc</li></ul></li><li><p><strong>Очищення STM32F412</strong></p></li><li><p>[<strong>BACKPORT</strong>] stm32f412: Виправлено підрахунок пінів</p></li><li><p>[<strong>BACKPORT</strong>] stm32f412: Замінено невдалу карту пінів на карту для SoC</p></li><li><p>[<strong>BACKPORT</strong>] stm32412: Виправлення карти пінів CAN1</p></li><li><p><strong>Оновлення безпеки</strong></p></li><li><p>[<strong>BACKPORT</strong>] tcp: Видалити неповну підтримку перезбірки TCP</p></li><li><p>[<strong>BACKPORT</strong>] net/tcp/tcp_input.c: Виправлення неправильної перевірки довжини спеціальних даних</p></li><li><p>[<strong>BACKPORT</strong>] libc: Додати додаткові перевірки до malloc realloc та memalign</p></li><li><p><strong>Виправлення IMXRT</strong></p></li><li><p>Додавання Single wire та правильних налаштувань парності до IMXRT для підтримки sbus та іншого.</p></li><li><p>[<strong>BACKPORT</strong>] imxrt:serial підтримка режиму single-wire</p></li><li><p>[<strong>BACKPORT</strong>] imxrt:imxrt_lowputc Виправлені налаштування парності.</p></li><li><p><strong>Покращення для STM32H7</strong></p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:SPI Виправлення 16-бітного режиму SPI</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:DMA BDMA не вимикається автоматично після завершення</p></li><li><p>[<strong>BACKPORT</strong>] Виправлення битих статичних даних в HEAP у SRAM4</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:SDMMC виправлення скидання do_gpio</p></li></ul><h3 id="uavcan" tabindex="-1">UAVCAN <a class="header-anchor" href="#uavcan" aria-label="Permalink to &quot;UAVCAN&quot;">​</a></h3><ul><li>UAVCANv0: Хоча базові функції, такі як оновлення прошивки та синхронізація параметрів вузлів CAN, були впроваджені більше 5 років тому, ми оновили підтримку, тепер, нарешті, пристрої є на ринку. Типові модулі CAN GPS, швидкості повітря та потужності підтримуються</li><li>UAVCANv0 Вузол: PX4 підтримував будівництво вузлів протягом багатьох років - тепер ми підтримуємо будівництво конкретних цілей, таких як одиниці GPS CUAV</li><li>UAVCANv1: Початковий альфа-варіант повної реалізації від кінця до кінця</li></ul>`,35),o=[i];function n(s,h,p,u,g,c){return r(),t("div",null,o)}const _=l(a,[["render",n]]);export{f as __pageData,_ as default};
