import{_ as e,c as t,o as a,ab as r}from"./chunks/framework.CUflZczI.js";const S=JSON.parse('{"title":"ROS 2","description":"","frontmatter":{},"headers":[],"relativePath":"uk/ros2/index.md","filePath":"uk/ros2/index.md"}'),o={name:"uk/ros2/index.md"},s=r('<h1 id="ros-2" tabindex="-1">ROS 2 <a class="header-anchor" href="#ros-2" aria-label="Permalink to &quot;ROS 2&quot;">​</a></h1><p><a href="https://index.ros.org/doc/ros2/" target="_blank" rel="noreferrer">ROS 2</a> є найновішою версією <a href="http://www.ros.org/" target="_blank" rel="noreferrer">ROS</a> (Robot Operating System), загальна мета бібліотеки робототехніки для якої може бути використана - це для створення потужних застосунків для дронів разом з PX4 Autopilot. Він охоплює більшість навчальних функцій і опцій <a href="./../ros/ros1.html">ROS 1</a> та покращує ряд недоліків попередньої версії.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Tip Команда розробників PX4 наполегливо рекомендуємо вам використовувати/перейти на цю версію ROS!</p></div><p>Спілкування між ROS 2 і PX4 використовує проміжне програмне забезпечення, яке реалізує протокол <a href="./../middleware/uxrce_dds.html">XRCE-DDS</a>. Цей посередник використовує PX4 повідомлення<a href="./../msg_docs/README.html">uORB messages</a> у вигляді повідомлень та типів ROS 2, що ефективно дозволяє прямий доступ до PX4 з робочих процесів та вузлів ROS 2. Проміжна програма використовує визначення повідомлень uORB для генерації коду для серіалізації та десеріалізації заголовків повідомлень PX4. Ці ж визначення повідомлень використовуються в програмах ROS 2, щоб дозволити інтерпретувати повідомлення.</p><p>To use the <a href="./../ros2/user_guide.html">ROS 2</a> over XRCE-DDS effectively, you must (at time of writing) have a reasonable understanding of the PX4 internal architecture and conventions, which differ from those used by ROS. У найближчому майбутньому ми плануємо надати ROS 2 API до абстрактних конвенцій PX4, разом із прикладами, що демонструють їх використання.</p><p>Основні теми в цьому розділі є:</p><ul><li><a href="./../ros2/user_guide.html">ROS 2 User Guide</a>: A PX4-centric overview of ROS 2, covering installation, setup, and how to build ROS 2 applications that communicate with PX4.</li><li><a href="./../ros2/offboard_control.html">ROS 2 приклад зовнішнього контролю</a></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>ROS 2 офіційно підтримується тільки на платформах Linux. Ubuntu 20.04 LTS є офіційним, підтримуваним дистрибутивом.</p></div><div class="info custom-block"><p class="custom-block-title">ROS 2 також може з’єднуватися з PX4 за допомогою <a href="https://github.com/mavlink/mavros/tree/ros2/mavros" target="_blank" rel="noreferrer">MAVROS</a> (замість XRCE-DDS). Ця опція підтримується проектом MAVROS.</p></div><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="./../ros2/user_guide.html">ROS 2 Посібник користувача</a></li><li><a href="./../middleware/uxrce_dds.html">XRCE-DDS (PX4-ROS 2/DDS Bridge)</a>: PX4 проміжне програмне забезпечення для підключення до ROS 2.</li></ul>',11),i=[s];function l(n,c,d,h,u,m){return a(),t("div",null,i)}const _=e(o,[["render",l]]);export{S as __pageData,_ as default};
