import{_ as a,c as e,o as r,ab as t}from"./chunks/framework.CUflZczI.js";const R=JSON.parse('{"title":"ROS 1 з MAVROS","description":"","frontmatter":{},"headers":[],"relativePath":"uk/ros/ros1.md","filePath":"uk/ros/ros1.md"}'),o={name:"uk/ros/ros1.md"},s=t('<h1 id="ros-1-з-mavros" tabindex="-1">ROS 1 з MAVROS <a class="header-anchor" href="#ros-1-з-mavros" aria-label="Permalink to &quot;ROS 1 з MAVROS&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The PX4 development team recommend that users migrate to <a href="./../ros2/">ROS 2</a> (i.e. skip this section)!</p></div><p><a href="./../ros/README.html">ROS</a> (Robot Operating System) це бібліотека загального призначення для робототехніки, яку можна використовувати для створення потужних додатків для дронів разом з PX4 Autopilot.</p><p>Цей розділ містить теми, пов&#39;язані з використанням &quot;оригінальної версії ROS&quot; та пакету <a href="./../ros/mavros_installation.html">MAVROS</a> для комунікації з PX4 через <a href="./../middleware/mavlink.html">MAVLink</a> (MAVROS переходить від ROS-тем до протоколу MAVLink та конвенцій PX4).</p><p>Основні охоплені тут теми:</p><ul><li><a href="./../ros/mavros_installation.html">ROS/MAVROS Installation Guide</a>: Налаштування середовища розробки PX4 з ROS 1 та MAVROS.</li><li><a href="./../ros/mavros_offboard_cpp.html">ROS/MAVROS (C++)</a>: Розділ, що показує основні концепції, пов&#39;язані з написанням C++ вузла MAVROS/ROS.</li><li><a href="./../ros/mavros_custom_messages.html">ROS/MAVROS Надсилання Custom Messages</a></li><li><a href="./../simulation/ros_interface.html">ROS з симулятором Gazebo Classic</a></li><li><a href="./../sim_gazebo_classic/octomap.html">Gazebo Classic OctoMap Models з ROS</a></li><li><a href="./../ros/raspberrypi_installation.html">Встановлення ROS на RPI</a></li><li><a href="./../ros/external_position_estimation.html">Оцінка зовнішньої позиції (заснована на спостережені/русі)</a></li></ul><h2 id="інші-ресурси" tabindex="-1">Інші ресурси <a class="header-anchor" href="#інші-ресурси" aria-label="Permalink to &quot;Інші ресурси&quot;">​</a></h2><ul><li><a href="./../ros/README.html#ros-setups">Системи PX4 ROS</a>.</li><li><a href="https://github.com/robin-shaun/XTDrone/blob/master/README.en.md" target="_blank" rel="noreferrer">XTDrone</a> - ROS + середовище симуляцій PX4 для комп&#39;ютерного зору. У посібнику <a href="https://www.yuque.com/xtdrone/manual_en" target="_blank" rel="noreferrer">XTDrone</a> є все необхідне для початку роботи!</li><li><a href="https://github.com/amov-lab/Prometheus/blob/master/README_EN.md" target="_blank" rel="noreferrer">Prometheus Autonomous Drone Project</a> - Prometheus це колекція програмних пакунків з ліцензією BSD-3 від <a href="https://github.com/amov-lab" target="_blank" rel="noreferrer">AMOVLab</a> на основі ROS 1, який надає повний набір рішень для розумного та автономного польоту дронів, таких як картографування, локалізація, планування, керування та виявлення цілі, повністю інтегроване з Симулятором <a href="./../sim_gazebo_classic/README.html">Gazebo Classic</a>.</li></ul>',8),l=[s];function i(n,m,h,c,_,u){return r(),e("div",null,l)}const S=a(o,[["render",i]]);export{R as __pageData,S as default};
