import{_ as e,a}from"./chunks/tfslot_integration.2zeY-sYi.js";import{_ as t,c as r,o as s,ab as i}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"TFSLOT датчик швидкості польоту ефекту Вентурі","description":"","frontmatter":{},"headers":[],"relativePath":"uk/sensor/airspeed_tfslot.md","filePath":"uk/sensor/airspeed_tfslot.md"}'),n={name:"uk/sensor/airspeed_tfslot.md"},l=i('<h1 id="tfslot-датчик-швидкості-польоту-ефекту-вентурі" tabindex="-1">TFSLOT датчик швидкості польоту ефекту Вентурі <a class="header-anchor" href="#tfslot-датчик-швидкості-польоту-ефекту-вентурі" aria-label="Permalink to &quot;TFSLOT датчик швидкості польоту ефекту Вентурі&quot;">​</a></h1><p><a href="https://github.com/ThunderFly-aerospace/TFSLOT01" target="_blank" rel="noreferrer">TFSLOT</a> це датчик швидкості повітря з відкритим вихідним кодом, що базується на <a href="https://en.wikipedia.org/wiki/Venturi_effect" target="_blank" rel="noreferrer">ефекті Вентурі</a>, який також має ІНС.</p><p><img src="'+e+'" alt="TFSLOT and TFSLOT WITH TFASPDIMU02 board"></p><p><a href="https://github.com/ThunderFly-aerospace/TFSLOT01" target="_blank" rel="noreferrer">TFSLOT</a> - це датчик швидкості повітря на основі вентурі ефектів. У базовій конфігурації TFSLOT обладнаний платою сенсорів <a href="https://github.com/ThunderFly-aerospace/TFASPDIMU02" target="_blank" rel="noreferrer">TFASPDIMU02</a>, яка містить диференційний датчик тиску (<a href="https://sensirion.com/products/catalog/?filter_series=d1816d53-f5c8-47e3-ab47-818c3fd54259" target="_blank" rel="noreferrer">серія Sensirion SDP3x</a>) та датчик відстеження руху 9-ти осей (<a href="https://invensense.tdk.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">ICM-20948</a>). Одиницю IMU можна використовувати як зовнішній компас.</p><ul><li>Цей дизайн має кілька переваг при використанні на невеликих і повільних безпілотних літальних апаратах.</li><li>Краща роздільна здатність при низьких швидкостях повітря (нижче 10 м/с).</li><li>Налаштована чутливість, яка надається зміною профілю.</li><li>Менша схильність до засмічення (наприклад, глиною після посадки)</li><li>Стійкий до погодних умов (дощ, сніг тощо)</li><li>Пряме інтегрування датчика різниці тиску без будь-яких додаткових трубок. Менше шансів на відмову датчика.</li><li>Можливість прямої інтеграції в конструкцію безпілотника. Дизайн є повністю відкритим джерелом.</li><li>Інтегрований зовнішній блок ІНС.</li></ul><p>Завдяки надрукованому трубці дуже легко змінити профіль вимірювання і таким чином змінити чутливість в певних діапазонах швидкості. У базовій формі воно оптимізовано так, що виміряний диференційний тиск відповідає тиску від труби Піто.</p><p><img src="'+a+`" alt="TFSLOT integrated in TF-G2"> <em>Перша інтеграція TFSLOT у автожир <a href="https://github.com/ThunderFly-aerospace/TF-G2/" target="_blank" rel="noreferrer">TF-G2</a></em></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Повна документація та вихідні файли можна знайти на <a href="https://github.com/ThunderFly-aerospace/TFSLOT01" target="_blank" rel="noreferrer">GitHub</a>.</p></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>TFSLOT можна придбати на <a href="https://www.tindie.com/products/thunderfly/tfslot01a-inovative-drone-airspeed-sensor/" target="_blank" rel="noreferrer">магазині Tindie</a> або надіславши нам запит електронною поштою на <a href="mailto:info@thunderfly.cz" target="_blank" rel="noreferrer">info@thunderfly.cz</a></p><h2 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h2><p><a href="https://github.com/ThunderFly-aerospace/TFASPDIMU02" target="_blank" rel="noreferrer">TFASPDIMU02</a> обладнаний роз&#39;ємом I2C JST-GH, який відповідає роз&#39;єму стандарту <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">стандарту дронів</a>. Отже, датчик може бути підключений безпосередньо до порту I2C автопілота за допомогою кабелю I2C 4pin JST-GH.</p><h2 id="конфігурація" tabindex="-1">Конфігурація <a class="header-anchor" href="#конфігурація" aria-label="Permalink to &quot;Конфігурація&quot;">​</a></h2><p>Оскільки перед датчиком має бути з&#39;єднана <a href="https://invensense.tdk.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">IMU IC</a>, ІК повинен бути встановлений в режимі мосту. Після цього можна запустити драйвер датчика швидкості повітря. Це може бути зроблено за допомогою наступних послідовностей команд. Команди передбачають підключення до порту I2C2.</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">icm20948_i2c_passthrough</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> start</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -X</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -b</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 2</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -a</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 0x68</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sdp3x_airspeed</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> start</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -X</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -b</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 2</span></span></code></pre></div><p>Цю послідовність можна зберегти на SD-картці у файлі <code>/etc/config.txt</code>. Ви можете дізнатися більше про конфігурацію на SD-картці на <a href="./../concept/system_startup.html#replacing-the-system-startup">окремій сторінці</a>.</p><p>Оскільки перетворення різниці тиску на швидкість повітря відрізняється від трубки Піто, цей профіль потребує змін. Це робиться шляхом встановлення параметра <a href="./../advanced_config/parameter_reference.html#CAL_AIR_CMODEL"><code>CAL_AIR_CMODEL</code></a> на 3 (датчик швидкості повітря на основі ефекту Вентурі)</p><h2 id="калібрування" tabindex="-1">Калібрування <a class="header-anchor" href="#калібрування" aria-label="Permalink to &quot;Калібрування&quot;">​</a></h2><p>Калібрування дещо складне через те, що поточна версія прошивки не підтримує калібрування з від&#39;ємними значеннями. Оскільки використовуваний датчик вимірює обидва напрямки повітряного потоку симетрично і має нульове зміщення, немає потреби калібрувати його знову перед кожним зльотом. Але вам потрібно забезпечити, щоб під час калібрування не було потоку повітря.</p><p>Найлегший спосіб калібрування - взяти клейку стрічку і приклеїти її між тиском на входах до датчика. Потім розпочніть процес калібрування і дмухайте ззаду, коли буде вимога. Якщо буде створено тиск принаймні 50 Па, калібрування буде успішним.</p>`,20),o=[l];function h(p,c,d,k,f,_){return s(),r("div",null,o)}const g=t(n,[["render",h]]);export{T as __pageData,g as default};
