import{_ as e}from"./chunks/leddar_one.COew240k.js";import{_ as a,c as t,o as r,ab as d}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"LeddarOne Lidar","description":"","frontmatter":{},"headers":[],"relativePath":"uk/sensor/leddar_one.md","filePath":"uk/sensor/leddar_one.md"}'),n={name:"uk/sensor/leddar_one.md"},o=d('<h1 id="leddarone-lidar" tabindex="-1">LeddarOne Lidar <a class="header-anchor" href="#leddarone-lidar" aria-label="Permalink to &quot;LeddarOne Lidar&quot;">​</a></h1><p><a href="https://leddartech.com/solutions/leddarone/" target="_blank" rel="noreferrer">LeddarOne</a> - це невеликий модуль Lidar з вузьким, але розсіяним променем, який пропонує відмінний загальний діапазон виявлення та продуктивність в міцному, надійному та економічному пакеті. Він має діапазон сенсора від (5см - 40м) і може бути підключений до портів PWM або I2C.</p><img src="'+e+'" alt="LeddarOne Lidar rangefinder" width="200px"><h2 id="налаштування-обладнання" tabindex="-1">Налаштування обладнання <a class="header-anchor" href="#налаштування-обладнання" aria-label="Permalink to &quot;Налаштування обладнання&quot;">​</a></h2><p>LeddarOne може бути підключений до будь-якого не використаного <em>серійного порту</em> (UART), наприклад: TELEM2, TELEM3, GPS2 тощо.</p><p>Побудуйте кабель, використовуючи плату та роз&#39;єм, а також роз&#39;єм LeddarOne (показаний нижче). Вам лише потрібно буде підключити контакти 5V, TX, RX та GND.</p><table><thead><tr><th>Pin</th><th>LeddarOne</th></tr></thead><tbody><tr><td>1</td><td>GND</td></tr><tr><td>2</td><td>-</td></tr><tr><td>3</td><td>VCC</td></tr><tr><td>4</td><td>RX</td></tr><tr><td>5</td><td>TX</td></tr><tr><td>6</td><td>-</td></tr></tbody></table><h2 id="налаштування-параметрів" tabindex="-1">Налаштування параметрів <a class="header-anchor" href="#налаштування-параметрів" aria-label="Permalink to &quot;Налаштування параметрів&quot;">​</a></h2><p><a href="./../peripherals/serial_configuration.html">Налаштуйте послідовний порт</a> на якому працюватиме лідар, використовуючи <a href="./../advanced_config/parameter_reference.html#SENS_LEDDAR1_CFG">SENS_LEDDAR1_CFG</a>. Немає потреби встановлювати швидкість передачі для порту, оскільки це налаштовано драйвером.</p><div class="info custom-block"><p class="custom-block-title">Якщо параметр конфігурації недоступний у <em>QGroundControl</em>, можливо, вам доведеться <a href="./../peripherals/serial_configuration.html#parameter_not_in_firmware">додати драйвер до мікропрограми</a>:</p><div class="language-plain vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">plain</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>CONFIG_DRIVERS_DISTANCE_SENSOR_LEDDAR_ONE=y</span></span></code></pre></div></div><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li><a href="https://leddartech.com/app/uploads/dlm_uploads/2021/04/Spec-Sheet_LeddarOne_V10.0_EN-1.pdf" target="_blank" rel="noreferrer">Специфікація LeddarOne</a></li></ul>',12),l=[o];function i(s,_,h,c,p,m){return r(),t("div",null,l)}const L=a(n,[["render",i]]);export{b as __pageData,L as default};
