import{_ as e}from"./chunks/tfmini_hero.D5WcvH71.js";import{_ as i,c as a,o as r,ab as n}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"Benewake TFmini Lidar","description":"","frontmatter":{},"headers":[],"relativePath":"uk/sensor/tfmini.md","filePath":"uk/sensor/tfmini.md"}'),t={name:"uk/sensor/tfmini.md"},o=n('<h1 id="benewake-tfmini-lidar" tabindex="-1">Benewake TFmini Lidar <a class="header-anchor" href="#benewake-tfmini-lidar" aria-label="Permalink to &quot;Benewake TFmini Lidar&quot;">​</a></h1><p>Лідар <em>Benewake TFmini</em> - це крихітний, недорогий та енергоефективний лідар з дальністю 12 м.</p><p>PX4 підтримує всі три варіанти серії: TFmini-s, TFmini-i, TFmini Plus. Ці повинні бути підключені до шини UART/серійного порту.</p><p><img src="'+e+'" alt="TFmini LiDAR"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://en.benewake.com/TFminiS/index_proid_325.html" target="_blank" rel="noreferrer">TFmini-s</a></li><li><a href="https://en.benewake.com/TFminii/index_proid_324.html" target="_blank" rel="noreferrer">TFmini-i</a> (промисловий)</li><li><a href="https://en.benewake.com/TFminiPlus/index_proid_323.html" target="_blank" rel="noreferrer">TFmini Plus</a></li></ul><h2 id="налаштування-обладнання" tabindex="-1">Налаштування обладнання <a class="header-anchor" href="#налаштування-обладнання" aria-label="Permalink to &quot;Налаштування обладнання&quot;">​</a></h2><p>TFmini може бути підключений до будь-якого не використаного <em>серійного порту</em> (UART), такого як: <code>TELEM2</code>, <code>TELEM3</code>, <code>GPS2</code> тощо.</p><h2 id="налаштування-параметрів" tabindex="-1">Налаштування параметрів <a class="header-anchor" href="#налаштування-параметрів" aria-label="Permalink to &quot;Налаштування параметрів&quot;">​</a></h2><p><a href="./../peripherals/serial_configuration.html">Налаштуйте послідовний порт</a> на якому працюватиме лідар, використовуючи <a href="./../advanced_config/parameter_reference.html#SENS_TFMINI_CFG">SENS_TFMINI_CFG</a>. Не потрібно встановлювати швидкість передачі (вона зашита в програму драйвера сенсора, оскільки підтримується лише одна швидкість).</p><p>:::note Якщо параметр конфігурації недоступний у <em>QGroundControl</em>, можливо, драйвер <a href="./../modules/modules_driver_distance_sensor.html#tfmini">tfmini</a> не входить до мікропрограми. Для отримання інформації про те, як її додати, див. :</p><ul><li><a href="./../peripherals/serial_configuration.html#parameter_not_in_firmware">Налаштування послідовного порту: Відсутній параметр конфігурації від QGroundControl</a></li><li><a href="./../hardware/porting_guide_config.html#px4-menuconfig-setup">Конфігурація дошки PX4 (Kconfig)</a>.</li></ul><p>:::</p>',13),l=[o];function m(s,_,d,h,c,p){return r(),a("div",null,l)}const k=i(t,[["render",m]]);export{T as __pageData,k as default};
