import{_ as e,c as a,o as t,ab as r}from"./chunks/framework.CUflZczI.js";const f=JSON.parse('{"title":"Розумні акумулятори","description":"","frontmatter":{},"headers":[],"relativePath":"uk/smart_batteries/index.md","filePath":"uk/smart_batteries/index.md"}'),i={name:"uk/smart_batteries/index.md"},s=r('<h1 id="розумні-акумулятори" tabindex="-1">Розумні акумулятори <a class="header-anchor" href="#розумні-акумулятори" aria-label="Permalink to &quot;Розумні акумулятори&quot;">​</a></h1><p>Розумні батареї надають більш точну (і часто більш детальну) інформацію про стан батареї, ніж автопілот може оцінити для «тупих» батарей. Це дозволяє забезпечити більш надійне сповіщення про умови випадкового відмовлення при плануванні польоту. Інформація може включати деякі з наступного: залишковий заряд, час до розряду (оцінений), напругу на елементах (оцінена максимальна/мінімальна, поточна напруга тощо), температуру, струми, інформацію про несправності, виробника батареї, хімічний склад тощо.</p><p>PX4 підтримує (принаймні) наступні розумні батарейки:</p><ul><li><a href="./../smart_batteries/rotoye_batmon.html">Rotoye Batmon</a></li></ul><h3 id="детальна-інформація" tabindex="-1">Детальна інформація <a class="header-anchor" href="#детальна-інформація" aria-label="Permalink to &quot;Детальна інформація&quot;">​</a></h3><ul><li><a href="https://mavlink.io/en/services/battery.html" target="_blank" rel="noreferrer">Протокол батареї Mavlink</a></li><li><a href="./../modules/modules_driver.html">batt_smbus</a> – Документи драйвера батареї PX4 SMBus</li><li><a href="./../config/safety.html#low-battery-failsafe">Безпека &gt; Захист від низького заряду батареї</a>.</li></ul>',6),o=[s];function l(n,_,d,c,h,m){return t(),a("div",null,o)}const p=e(i,[["render",l]]);export{f as __pageData,p as default};
