import{_ as r,a as e,b as a,c as o}from"./chunks/pico_configurator.PeWXcf7z.js";import{_ as t,c as i,o as l,ab as c}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"ARK Electron Microhard Серійне Телеметрійне Радіо","description":"","frontmatter":{},"headers":[],"relativePath":"uk/telemetry/ark_microhard_serial.md","filePath":"uk/telemetry/ark_microhard_serial.md"}'),n={name:"uk/telemetry/ark_microhard_serial.md"},h=c('<h1 id="ark-electron-microhard-серіине-телеметріине-радіо" tabindex="-1">ARK Electron Microhard Серійне Телеметрійне Радіо <a class="header-anchor" href="#ark-electron-microhard-серіине-телеметріине-радіо" aria-label="Permalink to &quot;ARK Electron Microhard Серійне Телеметрійне Радіо&quot;">​</a></h1><p><em>ARK Electron Мікрорадіопередавачі Microhard Serial Telemetry Radios</em> інтегрують модуль радіо <a href="http://microhardcorp.com/P900.php" target="_blank" rel="noreferrer">Microhard Pico Serial</a> P900 RF. Це може бути використано для активації комунікації MAVLink між радіо на транспортному засобі та GCS.</p><p>Радіостанції Microhard Pico Serial - це радіостанції з вихідною потужністю (до) 1 Вт, які підтримують режими точка-точка, точка-багато точок та мережеві режими. Радіопристрої Microhard Pico також можуть бути замовлені з шифруванням AES-256.</p><p>Приблизний діапазон з вихідною потужністю, встановленою на рівні 1 Вт, становить 8 км (5 миль), коли використовуються налаштування за замовчуванням. Одне радіо земної станції може бути використано для зв&#39;язку з кількома транспортними засобами за допомогою точка-багатоточної або мережевої топології меш. Транспортні засоби повинні мати різні ідентифікатори MAVLINK.</p><p><img src="'+r+'" alt="Радіо Microhard"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://arkelectron.com/product/1w-900mhz-serial-telemetry-air-radio/" target="_blank" rel="noreferrer">1W 900MHz Серійне Телеметрійне Радіо</a> (транспортний)</li><li><a href="https://arkelectron.com/product/1w-900mhz-serial-telemetry-ground-radio/" target="_blank" rel="noreferrer">1W 900МГц USB Серійне Телеметрійне Радіо</a> (наземна станція)</li><li><a href="https://arkelectron.com/product/1w-2400mhz-serial-telemetry-radio/" target="_blank" rel="noreferrer">1W 2.4ГГц Серійне Телеметрійне Радіо</a> (транспортний засіб)</li><li><a href="https://arkelectron.com/product/1w-2400mhz-usb-serial-telemetry-radio/" target="_blank" rel="noreferrer">1W 2.4ГГц USB Серійне Телеметрійне Радіо</a> (наземна станція)</li></ul><h2 id="з-єднання" tabindex="-1">З&#39;єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З&#39;єднання&quot;">​</a></h2><h3 id="транспортнии-радіопередавач" tabindex="-1">Транспортний радіопередавач <a class="header-anchor" href="#транспортнии-радіопередавач" aria-label="Permalink to &quot;Транспортний радіопередавач&quot;">​</a></h3><p>Підключіть радіо автомобіля до порту керування польотом <code>TELEM1</code>. Для цієї мети надається кабель телеметрії з 6 контактами JST GH стандарту Pixhawk.</p><p>Радіо може бути живлений за допомогою телеметричного кабелю, якщо вихідна потужність встановлена менше 100 мВт. Для вищих рівнів виводу радіо повинно бути окремо живлене через 2-контактний роз&#39;єм Molex Nano-Fit (тобто від батареї).</p><p><img src="'+e+'" alt="Microhard Radio on Vehicle"></p><h3 id="радіо-наземноі-станціі" tabindex="-1">Радіо наземної станції <a class="header-anchor" href="#радіо-наземноі-станціі" aria-label="Permalink to &quot;Радіо наземної станції&quot;">​</a></h3><p>Підключіть земельне радіо до земельної станції за допомогою USB C. Радіо не потребує окремого живлення при використанні USB PD (може бути постачана потужність 1 Вт).</p><h2 id="налаштування-конфігурація" tabindex="-1">Налаштування/Конфігурація <a class="header-anchor" href="#налаштування-конфігурація" aria-label="Permalink to &quot;Налаштування/Конфігурація&quot;">​</a></h2><p>Радіостанції налаштовані за замовчуванням на використання режиму peer-to-peer та швидкість передачі 57600 бод. Це дозволяє їм підключатися до порту <code>TELEM1</code> PX4 та <em>QGroundControl</em> <strong>без додаткової конфігурації</strong>.</p><p>::: note Ви можете використовувати іншу швидкість передачі даних, режим або порт контролера польоту. Єдине &quot;вимога&quot; - земний радіопередавач, повітряний радіопередавач, PX4 та QGroundControl повинні мати однакову швидкість передачі даних. :::</p><p>Радіопередавачі з віддаленою телеметрією <a href="./../telemetry/microhard_serial.html#configuration">Microhard Serial &gt; Налаштування</a> пояснює, як налаштувати радіопередавачі, <em>QGroundControl</em> та PX4.</p><p>Радіопристрої ARK Electron повинні бути підключені до комп&#39;ютера, на якому працює конфігураційний інструмент <em>PicoConfig</em>, як описано нижче:</p><ul><li><p>Для налаштування радіообладнання автомобіля вам доведеться підключити адаптер FTDI між конфігураційним портом JST-GH на 3 контакта радіо та Windows PC з програмою <em>Pico Config</em> (радіо повинно бути увімкнено, що можна зробити з батареї або через підключення даних до порту <code>TELEM1</code> автопілота).</p><p><img src="'+a+'" alt="Ark Microhard Serial - Ports"></p><p>Програма <em>Pico Config</em> автоматично виявить радіо. Налаштуйте швидкість передачі даних (бод-швидкість) так, щоб вона відповідала PX4 (і радіо земної станції).</p></li><li><p>З&#39;єднання USB C радіостанції наземної станції може бути використане для налаштування радіо (а також для телеметричних даних). Програма <em>Pico Config</em> автоматично виявить та підключиться до порту конфігурації. Налаштуйте параметри так, щоб швидкість передачі даних відповідала PX4.</p></li></ul><p>Як тільки радіостанції та PX4 будуть налаштовані на використання одного темпу передачі, ви зможете підключити QGroundControl до транспортного засобу через радіо.</p><h3 id="конфігурація-за-замовчуванням" tabindex="-1">Конфігурація за замовчуванням <a class="header-anchor" href="#конфігурація-за-замовчуванням" aria-label="Permalink to &quot;Конфігурація за замовчуванням&quot;">​</a></h3><p>Конфігурація радіо за замовчуванням, яка поставляється, показана в <em>PicoConfig</em> нижче.</p><p><img src="'+o+'" alt="Pico Config"></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://arkelectron.com/wp-content/uploads/2021/04/PicoConfig-1.7.zip" target="_blank" rel="noreferrer">Pico Config 1.7</a> - Інструмент конфігурації радіо</li></ul>',26),d=[h];function p(m,s,_,u,f,P){return l(),i("div",null,d)}const S=t(n,[["render",p]]);export{g as __pageData,S as default};
