import{_ as t,a as r,b as a,c as o,d as e,e as i,f as l,g as d}from"./chunks/holybro_microhard_dimension.C30evDyk.js";import{_ as h,a as c}from"./chunks/holybro_pico_config1.j1QXz-yX.js";import{_ as n,c as s,o as b,ab as m}from"./chunks/framework.CUflZczI.js";const H=JSON.parse('{"title":"Holybro Microhard P900 Радіо","description":"","frontmatter":{},"headers":[],"relativePath":"uk/telemetry/holybro_microhard_p900_radio.md","filePath":"uk/telemetry/holybro_microhard_p900_radio.md"}'),p={name:"uk/telemetry/holybro_microhard_p900_radio.md"},u=m('<h1 id="holybro-microhard-p900-радіо" tabindex="-1">Holybro Microhard P900 Радіо <a class="header-anchor" href="#holybro-microhard-p900-радіо" aria-label="Permalink to &quot;Holybro Microhard P900 Радіо&quot;">​</a></h1><p>Радіопередавач <a href="https://holybro.com/products/microhard-radio" target="_blank" rel="noreferrer">Holybro Microhard P900</a> інтегрує модуль радіо <a href="http://microhardcorp.com/P900.php" target="_blank" rel="noreferrer">Microhard Pico Serial</a> P900, який здатний забезпечувати високопродуктивний бездротовий серійний зв&#39;язок в надійних і безпечних мережах типу меш, точка-точка або точка-багатоточка. Він дозволяє комунікацію MAVLink між радіо на транспортному засобі та GCS.</p><p>Цей радіопередавач працює у діапазоні частот 902-928 МГц ISM, використовуючи технологію розподіленого спектра з переходом по частоті (FHSS), що забезпечує надійний бездротовий асинхронний передачу даних між більшістю типів обладнання, яке використовує послідовний інтерфейс. Радіопередавач може бути налаштований як основний координатор, вторинний координатор, резервний координатор або віддалений в мережевій топології меш, або як майстер(master,), ретранслятор(repeater) або віддалений в топології точка-точка(PP) або точка-багатоточка (PMP).</p><p>Ця універсальність дуже зручна для користувача. Цей радіопередавач налаштовується за допомогою AT-команд через порт даних або за допомогою програми <em>PicoConfig</em> через діагностичний порт.</p><p>Потужність передачі програмно вибирається від 100 мВт до 1 Вт, з дальністю до 40 миль. Одне радіо земної станції може бути використано для зв&#39;язку з кількома транспортними засобами за допомогою точка-багатоточної або мережевої топології меш. Транспортні засоби повинні мати різні ідентифікатори MAVLINK.</p><p><img src="'+t+'" alt="Holybro Microhard Радіо"></p><h2 id="функціі" tabindex="-1">Функції <a class="header-anchor" href="#функціі" aria-label="Permalink to &quot;Функції&quot;">​</a></h2><ul><li>Порт USB Type-C, інтегрований конвертер USB на UART</li><li>6-позиційний роз&#39;єм JST-GH, може бути безпосередньо підключений до порту TELEM на різних польотних контролерах стандарту Pixhawk, таких як <a href="./../flight_controller/pixhawk4.html">Pixhawk 4</a> та <a href="./../flight_controller/pixhawk5x.html">Pixhawk 5X</a></li><li>Високовольтний BEC на борту, Підтримка живлення напругою DC7~35V</li><li>Передача UART та індикатор ступеня сигналу RSSI з трьома етапами світлодіодів</li><li>Передача в межах громадського, ліцензійно-вільного діапазону радіочастот</li><li>Прозорий, низькій затримкою швидкості зв&#39;язку до 276 Кбіт/с</li><li>Підтримує потужну операцію із справжньою мережею Mesh з автоматичним маршрутизуванням</li><li>32 біти CRC, вибіркове перетранслювання та корекція помилок вперед</li><li>Окремий порт діагностики, прозора віддалена діагностика та онлайн контроль мережі</li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/microhard-radio" target="_blank" rel="noreferrer">Holybro Microhard P900 Telemetry Radio (100mW - 1W)</a></li></ul><h2 id="специфікація" tabindex="-1">Специфікація <a class="header-anchor" href="#специфікація" aria-label="Permalink to &quot;Специфікація&quot;">​</a></h2><img src="'+r+'" width="500px" title="Діагностика Holybro Microhard P900"><h2 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h2><h4 id="транспортнии-радіопередавач" tabindex="-1">Транспортний радіопередавач <a class="header-anchor" href="#транспортнии-радіопередавач" aria-label="Permalink to &quot;Транспортний радіопередавач&quot;">​</a></h4><p>Цей радіопередавач поставляється з кабелем JST GH з 6 контактами, який можна підключити до порту <code>TELEM1</code> на контролерах польоту, які відповідають стандарту роз&#39;єму Pixhawk. Радіопередавач повинен окремо живитися через кабель живлення з 4-контактним роз&#39;ємом JST-GH XT30 (7-35VDC).</p><h4 id="земна-станція-радіопередавача" tabindex="-1">Земна станція радіопередавача <a class="header-anchor" href="#земна-станція-радіопередавача" aria-label="Permalink to &quot;Земна станція радіопередавача&quot;">​</a></h4><p>У цього радіопередавача є внутрішній конвертер USB в UART, земний радіопередавач може бути підключений до земної станції через USB C. Радіопередавач повинен окремо живитися через кабель живлення XT30 (7-35VDC).</p><h2 id="налаштування-конфігурація" tabindex="-1">Налаштування/Конфігурація <a class="header-anchor" href="#налаштування-конфігурація" aria-label="Permalink to &quot;Налаштування/Конфігурація&quot;">​</a></h2><p>Радіопередавачі Holybro Microhard P900 налаштовані на режим роботи точка-точка та швидкість передачі даних 57600 бод в заводських умовах. Це дозволяє їм підключатися до порту <code>TELEM1</code> PX4 та <em>QGroundControl</em> <strong>без додаткової конфігурації</strong>.</p><p>::: note Ви можете використовувати іншу швидкість передачі даних, режим або порт контролера польоту. Єдине &quot;вимога&quot; - земний радіопередавач, повітряний радіопередавач, PX4 та QGroundControl повинні мати однакову швидкість передачі даних. :::</p><p>Радіопередавачі з віддаленою телеметрією <a href="./../telemetry/microhard_serial.html#configuration">Microhard Serial &gt; Налаштування</a> пояснює, як налаштувати радіопередавачі, <em>QGroundControl</em> та PX4.</p><p>Для налаштування радіопередавачів за допомогою програми <em>PicoConfig</em> (як описано за посиланням вище) вона повинна бути підключена через діагностичний порт:</p><img src="'+a+'" width="500px" title="Діагностика Holybro Microhard P900"><p>Діагностичний порт використовує роз&#39;єм JST SH з 4 позиціями. Якщо ви використовуєте програму <em>PicoConfig</em> або спеціальні діагностичні команди для налаштування радіопередавача, ви повинні підключитися до цього порту. Діагностичний порт сумісний з логічним рівнем напруги 3,3 В. Для підключення радіопередавача до комп&#39;ютера потрібна плата USB-серійного перетворювача. Ви можете придбати конвертер <a href="https://holybro.com/products/uart-to-usb-converter" target="_blank" rel="noreferrer">Holybro UART to USB</a>.</p><p>Програма <em>Pico Config</em> автоматично виявить і підключиться до порту налаштування. Налаштуйте параметри так, щоб швидкість передачі даних відповідала PX4 (та земній станції).</p><p>Утримуючи кнопку <strong>Конфігурація</strong> під час увімкнення радіостанції, пристрій перейде в режим КОМАНД: буде активовано послідовний інтерфейс за замовчуванням і тимчасово налаштовано на роботу з налаштуваннями послідовного порту за замовчуванням 9600/8/N/1.</p><p>Зверніть увагу, що також можна налаштувати радіоприймачі за допомогою команд AT в порту.</p><h3 id="конфігурація-за-замовчуванням" tabindex="-1">Конфігурація за замовчуванням <a class="header-anchor" href="#конфігурація-за-замовчуванням" aria-label="Permalink to &quot;Конфігурація за замовчуванням&quot;">​</a></h3><p>Конфігурація радіо за замовчуванням, яка постачається, показана в <em>PicoConfig</em> нижче (може знадобитися повторно налаштувати їх, як показано після оновлення мікропрограми або скидання радіо).</p><img src="'+h+'" width="400px" title="Holybro Pico Config"><img src="'+c+'" width="400px" title="Holybro Pico Config"><p>У режимах роботи «точка-точка» для забезпечення мережевої синхронізації системи має бути головний пристрій, тому один радіоприймач має бути налаштований на головний PP, а інший — на віддалений PP.</p><h2 id="led-статус" tabindex="-1">LED-статус <a class="header-anchor" href="#led-статус" aria-label="Permalink to &quot;LED-статус&quot;">​</a></h2><img src="'+o+'" width="600px" title="Holybro Pico Config"><p>Радіо P900 має 6 світлодіодів статусу: три з них сині, два помаранчеві, а один зелений. Інтерпретація різних станів світлодіодів LEDs:</p><ul><li>Power LED(green) <ul><li>Цей світлодіод буде світитися, коли радіо P900 підключено до джерела живлення (7-35VDC).</li></ul></li><li>TX LED (orange) <ul><li>Коли підсвічується, цей світлодіод вказує на те, що радіо передає дані повітрям.</li></ul></li><li>RX LED (orange) <ul><li>Цей світлодіод показує, що радіо синхронізовано і отримано дійсні пакети.</li></ul></li><li>RSSI LEDs (3x Blue) <ul><li>По мірі збільшення отриманої сили сигналу, починаючи з найлівішого, кількість активних</li></ul></li><li>RSSI LEDs збільшуються. Сила сигналу обчислюється на основі останніх чотирьох отриманих пакетів з правильним CRC. Значення RSSI повідомляється в S123.</li></ul><img src="'+e+'" width="600px" title="Holybro Pico Config"><img src="'+i+'" width="600px" title="Holybro Pico Config"><h3 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h3><h4 id="порт-діагностики" tabindex="-1">Порт діагностики <a class="header-anchor" href="#порт-діагностики" aria-label="Permalink to &quot;Порт діагностики&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Напруга</th></tr></thead><tbody><tr><td>1</td><td>NC</td><td>--</td></tr><tr><td>2 (black)</td><td>RX</td><td>+3.3V</td></tr><tr><td>3 (black)</td><td>TX</td><td>+3.3V</td></tr><tr><td>4 (black)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="порт-даних" tabindex="-1">Порт даних <a class="header-anchor" href="#порт-даних" aria-label="Permalink to &quot;Порт даних&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Напруга</th></tr></thead><tbody><tr><td>1 (red)</td><td>NC</td><td>--</td></tr><tr><td>2 (black)</td><td>RX</td><td>+3.3V</td></tr><tr><td>3 (black)</td><td>TX</td><td>+3.3V</td></tr><tr><td>4 (black)</td><td>CTS</td><td>+3.3V</td></tr><tr><td>5 (black)</td><td>RTS</td><td>+3.3V</td></tr><tr><td>6 (black)</td><td>GND</td><td>GND</td></tr></tbody></table><h4 id="порт-живлення" tabindex="-1">Порт живлення <a class="header-anchor" href="#порт-живлення" aria-label="Permalink to &quot;Порт живлення&quot;">​</a></h4><table><thead><tr><th>Pin</th><th>Сигнал</th><th>Напруга</th></tr></thead><tbody><tr><td>1(red)</td><td>BAT+</td><td>7-35V</td></tr><tr><td>2 (red)</td><td>BAT+</td><td>7-35V</td></tr><tr><td>3 (black)</td><td>BAT-</td><td>GND</td></tr><tr><td>4 (black)</td><td>BAT-</td><td>GND</td></tr></tbody></table><img src="'+l+'" width="600px" title="Holybro Pico Config"><h3 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h3><img src="'+d+'" width="600px" title="Holybro Pico Config"><h3 id="споживання-електроенергіі" tabindex="-1">Споживання електроенергії <a class="header-anchor" href="#споживання-електроенергіі" aria-label="Permalink to &quot;Споживання електроенергії&quot;">​</a></h3><ul><li>Напруга живлення: DC7~35V від 4-контактного роз&#39;єму JST-GH до XT30 (включено)</li><li>Передавати поточний: 200 мА / 7 В при 20 дБм <ul><li>350мА/7В при 27дБм</li><li>800 мА/7V при 30 дБм</li></ul></li><li>Отримуваний струм: 100 мА</li><li>Вага: 42 г (без антени)</li></ul><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/telemetry-radio/microhard-radio" target="_blank" rel="noreferrer">Microhard Радіо</a> (docs.holybro.com)</li><li><a href="https://docs.holybro.com/telemetry-radio/microhard-radio/download" target="_blank" rel="noreferrer">Завантаження Holybro Microhard P900</a> (посібники та інші документи) (Holybro)</li></ul>',52),_=[u];function P(f,k,y,g,x,q){return b(),s("div",null,_)}const D=n(p,[["render",P]]);export{H as __pageData,D as default};
