import{_ as i,a,b as l}from"./chunks/holybro_sik_radio_v3_include.CrDBSbSv.js";import{_ as e,c as o,o as r,ab as t}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"HolyBro Телеметричне радіо","description":"","frontmatter":{},"headers":[],"relativePath":"uk/telemetry/holybro_sik_radio.md","filePath":"uk/telemetry/holybro_sik_radio.md"}'),h={name:"uk/telemetry/holybro_sik_radio.md"},n=t('<h1 id="holybro-телеметричне-радіо" tabindex="-1">HolyBro Телеметричне радіо <a class="header-anchor" href="#holybro-телеметричне-радіо" aria-label="Permalink to &quot;HolyBro Телеметричне радіо&quot;">​</a></h1><p>Цей Holybro <a href="./../telemetry/sik_radio.html">SiK</a> Телеметрійний радіо зв&#39;язок є невеликим, легким і недорогим відкритим джерелом платформа &quot;plug-and-play&quot; для підключення вашого автопілотного контролера польоту та земної станції.</p><p>Це радіо - &quot;plug-and-play&quot; з усіма контролерами, що працюють у PX4 і мають JST-GH 6 Pin Connector TELEM порт (<a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>). Це надає найлегший спосіб налаштування телеметрійного з&#39;єднання між вашим контролером польоту та наземною станцією. Він використовує open source прошивку, яка була спеціально розроблена для успішної роботи з пакетами MAVLink та інтеграції з QGroundControl &amp; PX4 Autopilot.</p><p>Вони, як правило, дозволяють межі краще, ніж 300 &quot;поза коробкою&quot; (діапазон можна поширити на кілька кілометрів із використанням патч-антени на землі).</p><p>Радіоприймач може бути 915 МГц (США), або 433 МГц з кроком (ЄС, Азія, Африка Океанія). Зверніть увагу, що регіони, зазначені вище, є орієнтовними, і вам слід перевірити правила для вашої країни.</p><img src="'+i+'" width="600px" title="Sik Telemetry Radio"><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://holybro.com/collections/telemetry-radios/products/sik-telemetry-radio-v3" target="_blank" rel="noreferrer">HolyBro (Sik) Телеметричне радіо V3</a></li></ul><h2 id="особливості" tabindex="-1">Особливості <a class="header-anchor" href="#особливості" aria-label="Permalink to &quot;Особливості&quot;">​</a></h2><ul><li>Відкрите програмне забезпечення SiK</li><li>Plug-n-play для стандартного контролера польоту Pixhawk</li><li>Найлегший спосіб підключити ваш автопілот та наземну станцію</li><li>Можливість взаємозамінності радіо на повітрі та на землі</li><li>Порт Micro-USB (кабель адаптера Type-C включено)</li><li>6-позиційний роз&#39;єм JST-GH</li></ul><h2 id="специфікація" tabindex="-1">Специфікація <a class="header-anchor" href="#специфікація" aria-label="Permalink to &quot;Специфікація&quot;">​</a></h2><ul><li>Максимальна вихідна потужність 100 мВт (налаштовувана) -117 дБм чутливість прийому</li><li>РП-SMA роз&#39;єм</li><li>Двосторонній повний дуплексний зв&#39;язок через адаптивний інтерфейс TDM UART</li><li>Прозоре послідовне посилання</li><li>Формування протоколу MAVLink</li><li>Частотно-перестроювана розподілена спектральна модуляція (FHSS) Налаштований цикл роботи</li><li>Корекція помилок виправляє до 25% помилок бітів Відкрите програмне забезпечення SIK</li><li>Налаштовується через Mission Planner &amp; APM Planner</li><li>FT230X USB на BASIC UART IC</li></ul><h2 id="стан-індикатора-світлодіодів" tabindex="-1">Стан індикатора світлодіодів <a class="header-anchor" href="#стан-індикатора-світлодіодів" aria-label="Permalink to &quot;Стан індикатора світлодіодів&quot;">​</a></h2><p>Радіоприймачі мають 2 індикатори стану, один червоний і один зелений. Інтерпретація різних станів світлодіодів:</p><ul><li>Зелений світлодіод мигає - пошук іншого радіо</li><li>Зелений світлодіод постійно горить - з&#39;єднання встановлено з іншим радіо</li><li>Червоний світлодіод мигає - передача даних</li><li>Червоний світлодіод постійний - у режимі оновлення прошивки</li></ul><img src="'+a+'" width="500px" title="Pixhawk5x Upright Image"><h2 id="підключення-до-політного-контролера" tabindex="-1">Підключення до Політного Контролера <a class="header-anchor" href="#підключення-до-політного-контролера" aria-label="Permalink to &quot;Підключення до Політного Контролера&quot;">​</a></h2><p>Використовуйте 6-контактний роз&#39;єм JST-GH, який поставляється з радіо, щоб підключити радіо до порту <code>TELEM1</code> вашого контролера польоту (можна також використовувати <code>TELEM2</code>, але рекомендація за замовчуванням - <code>TELEM1</code>).</p><h2 id="підключення-до-пк-або-наземноі-станціі" tabindex="-1">Підключення до ПК або Наземної станції <a class="header-anchor" href="#підключення-до-пк-або-наземноі-станціі" aria-label="Permalink to &quot;Підключення до ПК або Наземної станції&quot;">​</a></h2><p>Підключення радіо до вашого ПК з Windows або станції землі є настільки простим, як підключення кабелю USB мікро/типу-C (адаптер Type-C включений до радіо) до вашого ПК/станції землі.</p><p>Необхідні драйвери повинні бути встановлені автоматично, і радіо з&#39;явиться як новий «USB-серійний порт» в диспетчері пристроїв Windows у розділі Порти (COM &amp; LPT). Випадаючий список вибору порту COM планувальника місій також повинен містити той самий новий порт COM.</p><h2 id="пакет-включає" tabindex="-1">Пакет включає <a class="header-anchor" href="#пакет-включає" aria-label="Permalink to &quot;Пакет включає&quot;">​</a></h2><ul><li>Радіомодулі з антенами *2</li><li>Мікро-USB для USB-кабелю *1</li><li>Кабель-переходник Micro-USB на Micro-USB OTG *1</li><li>Адаптер Micro USB до Type C</li><li>Кабель JST-GH-6P до кабелю JST-GH-6P *1 (для Pixhawk Standard FC)</li><li>JST-GH-6P до Molex DF12 6P (для Pix32, Pixhawk 2.4.6, тощо)</li></ul><img src="'+l+'" width="600px" title="Sik Telemetry Radio">',24),d=[n];function s(c,p,u,_,m,k){return r(),o("div",null,d)}const T=e(h,[["render",s]]);export{b as __pageData,T as default};
