import{_ as e}from"./chunks/holybro_sik_radio.BqdrnNgc.js";import{_ as a,c as r,o as t,ab as i}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"SiK Radio","description":"","frontmatter":{},"headers":[],"relativePath":"uk/telemetry/sik_radio.md","filePath":"uk/telemetry/sik_radio.md"}'),o={name:"uk/telemetry/sik_radio.md"},l=i('<h1 id="sik-radio" tabindex="-1">SiK Radio <a class="header-anchor" href="#sik-radio" aria-label="Permalink to &quot;SiK Radio&quot;">​</a></h1><p><a href="https://github.com/LorenzMeier/SiK" target="_blank" rel="noreferrer">SiK radio</a> – це набір програмного забезпечення та інструментів для телеметричних радіостанцій.</p><p>PX4 сумісний за протоколом із радіостанціями, які використовують <em>SiK</em>. Радіостанції SiK часто постачаються з відповідними роз’ємами/кабелями, які дозволяють безпосередньо підключати їх до контролерів <a href="./../flight_controller/pixhawk_series.html">Pixhawk</a> (у деяких випадках може знадобитися придбати відповідний кабель/роз’єм). Зазвичай вам знадобиться пара пристроїв: один для транспортного засобу та один для наземної станції.</p><p>Апаратне забезпечення для радіо SiK можна придбати у різних виробників/магазинів у варіантах, які підтримують різні діапазони та форм-фактори.</p><p><img src="'+e+'" alt="SiK Radio"></p><h2 id="постачальники" tabindex="-1">Постачальники <a class="header-anchor" href="#постачальники" aria-label="Permalink to &quot;Постачальники&quot;">​</a></h2><ul><li><a href="./../telemetry/rfd900_telemetry.html">RFD900 Телеметричне радіо</a></li><li><a href="./../telemetry/holybro_sik_radio.html">Holybro Телеметричне радіо</a></li><li><del><em>HKPilot Телеметричне радіо</em></del> (Припинено)</li><li><del><em>3DR Телеметричне радіо</em></del> (припинено)</li></ul><h2 id="налаштування-конфігурація" tabindex="-1">Налаштування/конфігурація <a class="header-anchor" href="#налаштування-конфігурація" aria-label="Permalink to &quot;Налаштування/конфігурація&quot;">​</a></h2><p>Підземне радіо, орієнтоване на станції підключено через USB (по суті plug-n-play).</p><p>Автомобільний радіоприймач під’єднується до порту <code>TELEM1</code> контролера польоту, і зазвичай не потребує подальшого налаштування.</p><h2 id="оновлення-прошивки" tabindex="-1">Оновлення прошивки <a class="header-anchor" href="#оновлення-прошивки" aria-label="Permalink to &quot;Оновлення прошивки&quot;">​</a></h2><p>Апаратне забезпечення більшості <a href="#vendors">постачальників</a> має попередньо налаштовано з останньою прошивкою. Можливо, вам знадобиться оновити старе обладнання новим мікропрограмним забезпеченням, наприклад, щоб отримати підтримку MAVLink 2.</p><p>Ви можете оновити мікропрограму радіо за допомогою <em>QGroundControl</em>: <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/firmware.html" target="_blank" rel="noreferrer">Посібник користувача QGroundControl &gt; Завантаження мікропрограми</a>.</p><h2 id="розширене-налаштування-конфігурація" tabindex="-1">Розширене налаштування/конфігурація <a class="header-anchor" href="#розширене-налаштування-конфігурація" aria-label="Permalink to &quot;Розширене налаштування/конфігурація&quot;">​</a></h2><p>Розділ «Розробка» містить <a href="./../data_links/sik_radio.html">додаткову інформацію</a> про створення мікропрограми та конфігурації на основі AT-команд. Це не повинно вимагатися особам, які не є розробниками.</p>',15),d=[l];function s(h,n,_,m,c,p){return t(),r("div",null,d)}const b=a(o,[["render",s]]);export{k as __pageData,b as default};
