import{_ as a,c as e,o as t,ab as _}from"./chunks/framework.CUflZczI.js";const u=JSON.parse('{"title":"Тест MC_03 - поєднання автоматичного і ручного керування","description":"","frontmatter":{},"headers":[],"relativePath":"uk/test_cards/mc_03_auto_manual_mix.md","filePath":"uk/test_cards/mc_03_auto_manual_mix.md"}'),r={name:"uk/test_cards/mc_03_auto_manual_mix.md"},o=_('<h1 id="тест-mc-03-поєднання-автоматичного-і-ручного-керування" tabindex="-1">Тест MC_03 - поєднання автоматичного і ручного керування <a class="header-anchor" href="#тест-mc-03-поєднання-автоматичного-і-ручного-керування" aria-label="Permalink to &quot;Тест MC_03 - поєднання автоматичного і ручного керування&quot;">​</a></h1><h2 id="створити-та-завантажити-місію" tabindex="-1">Створити та завантажити місію <a class="header-anchor" href="#створити-та-завантажити-місію" aria-label="Permalink to &quot;Створити та завантажити місію&quot;">​</a></h2><p>❏ Критерії Місії</p><p>    ❏ Зміни висоти протягом місії</p><p>    ❏ Місія має завершуватися в повітрі, а НЕ на землі/RTL</p><p>    ❏ Тривалість від 3 до 4 хвилин</p><p>Завантаження місії на машину за допомогою QGroundControl</p><h2 id="політ" tabindex="-1">Політ <a class="header-anchor" href="#політ" aria-label="Permalink to &quot;Політ&quot;">​</a></h2><p>❏ Постановка на озброєння та зліт у режимі позиції</p><p>❏ Автозалучення</p><p>❏ Слідкуйте за відстеженням і поворотами</p><p>❏ Після завершення місії поверніться в режим розташування</p><p>    ❏ Горизонтальне положення має містити поточне значення з стрижнем у центрі</p><p>    ❏ Вертикальна позиція має містити поточне значення з джойстиком у центрі</p><p>    ❏ Реакцію дросельної заслінки встановлено на швидкість підйому/спуску</p><p>    ❏ Pitch/Roll/Yaw відповідь встановлена на відмітку Pitch/Roll/Yaw</p><p>❏ Використовуйте RTL</p><p>❏ Після торкання землі коптер повинен автоматично знятися з охорони протягом 2 секунд (час зняття з охорони встановлюється параметром: <a href="./../advanced_config/parameter_reference.html#COM_DISARM_LAND">COM_DISARM_LAND</a>)</p><h2 id="очікувані-результати" tabindex="-1">Очікувані результати <a class="header-anchor" href="#очікувані-результати" aria-label="Permalink to &quot;Очікувані результати&quot;">​</a></h2><ul><li>Зліт повинен бути плавним, коли газ піднято</li><li>Немає коливання в жодному з перерахованих режимів польоту</li><li>Після посадки, коптер не повинен підскакувати на землі</li></ul>',20),l=[o];function i(p,c,n,s,d,h){return t(),e("div",null,l)}const f=a(r,[["render",i]]);export{u as __pageData,f as default};
