import{_ as a,c as e,o as t,ab as l}from"./chunks/framework.CUflZczI.js";const u=JSON.parse('{"title":"Тест MC_05 - Політ у приміщенні (ручні режими)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/test_cards/mc_05_indoor_flight_manual_modes.md","filePath":"uk/test_cards/mc_05_indoor_flight_manual_modes.md"}'),i={name:"uk/test_cards/mc_05_indoor_flight_manual_modes.md"},r=l('<h1 id="тест-mc-05-політ-у-приміщенні-ручні-режими" tabindex="-1">Тест MC_05 - Політ у приміщенні (ручні режими) <a class="header-anchor" href="#тест-mc-05-політ-у-приміщенні-ручні-режими" aria-label="Permalink to &quot;Тест MC_05 - Політ у приміщенні (ручні режими)&quot;">​</a></h1><h2 id="коли-використовувати-цю-тестову-картку" tabindex="-1">Коли використовувати цю тестову картку <a class="header-anchor" href="#коли-використовувати-цю-тестову-картку" aria-label="Permalink to &quot;Коли використовувати цю тестову картку&quot;">​</a></h2><ul><li>Перший політ нової конструкції</li><li>Коли необхідно повторити проблему в обмеженій області</li><li>Експериментальні збірки, які можуть мати проблеми стабільності</li><li>Тестування обладнання, яке було замінено та/або модифіковано</li></ul><h2 id="озброєння-та-зліт" tabindex="-1">Озброєння та зліт <a class="header-anchor" href="#озброєння-та-зліт" aria-label="Permalink to &quot;Озброєння та зліт&quot;">​</a></h2><p>❏ Встановіть режим польоту на стабілізацію та запуск</p><p>❏ Зліт, піднявши дросель</p><h2 id="політ" tabindex="-1">Політ <a class="header-anchor" href="#політ" aria-label="Permalink to &quot;Політ&quot;">​</a></h2><p>❏ Стабілізований</p><p>    ❏ Відповідь на нахил/нахил/поворот 1:1</p><p>    ❏ Реакція дросельної заслінки 1:1 (Реакція на газ 1:1)</p><p>❏ Висота</p><p>    Вертикальна позиція повинна містити поточне значення з ручкою в центрі</p><p>    Pitch/Roll/Yaw відповідь 1:1</p><p>    ❏ Відповідь дроселя встановлено на швидкість підйому / спуску</p><h2 id="посадка" tabindex="-1">Посадка <a class="header-anchor" href="#посадка" aria-label="Permalink to &quot;Посадка&quot;">​</a></h2><p>❏ Посадка в режимі Стабілізації або на висоті з дроселем нижче 40%</p><p>❏ При дотику до землі коптер автоматично роззброюється протягом 2 секунд (час роззброювання встановлений параметром: <a href="./../advanced_config/parameter_reference.html#COM_DISARM_LAND">COM_DISARM_LAND</a>)</p><h2 id="очікувані-результати" tabindex="-1">Очікувані результати <a class="header-anchor" href="#очікувані-результати" aria-label="Permalink to &quot;Очікувані результати&quot;">​</a></h2><ul><li>Зліт повинен бути плавним, коли газ піднято</li><li>Немає коливання в жодному з перерахованих режимів польоту</li><li>Після посадки, коптер не повинен підскакувати на землі</li></ul>',19),o=[r];function _(n,c,d,h,s,p){return t(),e("div",null,o)}const f=a(i,[["render",_]]);export{u as __pageData,f as default};
