import{_ as a,a as i}from"./chunks/driver_sbus_signal_inverter_breadboard.DRHprF0A.js";import{_ as s,c as t,o as e,ab as l}from"./chunks/framework.CUflZczI.js";const S=JSON.parse('{"title":"Підключення RC-приймача до автопілота на базі Linux PX4","description":"","frontmatter":{},"headers":[],"relativePath":"uk/tutorials/linux_sbus.md","filePath":"uk/tutorials/linux_sbus.md"}'),r={name:"uk/tutorials/linux_sbus.md"},o=l('<h1 id="підключення-rc-приимача-до-автопілота-на-базі-linux-px4" tabindex="-1">Підключення RC-приймача до автопілота на базі Linux PX4 <a class="header-anchor" href="#підключення-rc-приимача-до-автопілота-на-базі-linux-px4" aria-label="Permalink to &quot;Підключення RC-приймача до автопілота на базі Linux PX4&quot;">​</a></h1><p>Ця тема показує, як налаштувати автопілот на базі Linux PX4 для підключення та використання <a href="./../getting_started/rc_transmitter_receiver.html"> підтримуваного RC-приймача</a> на будь-якому серійному порту</p><p>Для типів RC, крім S.Bus, ви можете просто під&#39;єднати приймач безпосередньо до серійних портів або до USB через USB до TTY серійного кабелю (наприклад, PL2302 USB в Serial TTL)</p><div class="info custom-block"><p class="custom-block-title">Для отримувача S.Bus (або кодера - наприклад, від Futaba, RadioLink тощо) вам зазвичай потрібно підключити приймач та пристрій через <a href="#signal_inverter_circuit">схему інвертування сигналу</a>, але в іншому випадку налаштування залишається таким же.</p></div><p>Тоді <a href="#start_driver">Запустіть PX4 RC драйвер</a> на пристрої, як показано нижче.</p><p><a id="start_driver"></a></p><h2 id="запуск-драивера" tabindex="-1">Запуск драйвера <a class="header-anchor" href="#запуск-драивера" aria-label="Permalink to &quot;Запуск драйвера&quot;">​</a></h2><p>Щоб запустити драйвер RC в певному UART (наприклад, в цьому випадку <code>/dev/ttyS2</code>):</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">rc_input</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> start</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -d</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /dev/ttyS2</span></span></code></pre></div><p>Для іншої інформації про використання драйвера дивися: <a href="./../modules/modules_driver.html#rc-input">rc_input</a>.</p><p><a id="signal_inverter_circuit"></a></p><h2 id="схема-інвертування-сигналу-лише-для-s-bus" tabindex="-1">Схема інвертування сигналу (лише для S.Bus) <a class="header-anchor" href="#схема-інвертування-сигналу-лише-для-s-bus" aria-label="Permalink to &quot;Схема інвертування сигналу (лише для S.Bus)&quot;">​</a></h2><p>S.Bus - це <em>інвертований</em> сигнал комунікації UART.</p><p>Хоча деякі серійні порти / контролери польоту можуть читати інвертований сигнал UART, більшість вимагає схеми інвертування сигналу між приймачем та серійним портом для деінвертації сигналу.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Ця схема також необхідна для зчитування сигналів дистанційного керування S.Bus через послідовний порт або USB-до-TTY конвертер послідовного порту.</p></div><p>У цьому розділі показано, як створити відповідну схему.</p><h3 id="необхідні-компоненти" tabindex="-1">Необхідні компоненти <a class="header-anchor" href="#необхідні-компоненти" aria-label="Permalink to &quot;Необхідні компоненти&quot;">​</a></h3><ul><li>1x NPN транзистор (наприклад, NPN S9014 TO92)</li><li>1x 10K резистор</li><li>1x 1K резистор</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Можна використовувати будь-який тип/модель транзистора, оскільки струм споживання дуже низький.</p></div><h3 id="схема-діаграми-підключення" tabindex="-1">Схема Діаграми/Підключення <a class="header-anchor" href="#схема-діаграми-підключення" aria-label="Permalink to &quot;Схема Діаграми/Підключення&quot;">​</a></h3><p>Підключіть компоненти, як описано нижче (і показано на схемі):</p><ul><li>S.Bus сигнал → 1K резистор → база NPN транзистора</li><li>NPN транзистора емітер → GND</li><li>3.3VCC → резистор 10K → колектор NPN транзистора → rxd USB-to-TTY</li><li>5.0VCC → S.Bus VCC</li><li>GND → S.Bus GND</li></ul><p><img src="'+a+'" alt="Signal inverter circuit diagram"></p><p>На зображенні нижче показано підключення на дошці для макету.</p><p><img src="'+i+'" alt="Signal inverter breadboard"></p>',25),p=[o];function n(c,u,d,h,_,m){return e(),t("div",null,p)}const x=s(r,[["render",n]]);export{S as __pageData,x as default};
