import{_ as e}from"./chunks/t265_intel_realsense_tracking_camera_photo_angle.BpsSr1zT.js";import{_ as a,c as t,o as s,ab as i}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Intel® RealSense™ Tracking Camera T265 (VIO)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/camera/camera_intel_realsense_t265_vio.md","filePath":"zh/camera/camera_intel_realsense_t265_vio.md"}'),r={name:"zh/camera/camera_intel_realsense_t265_vio.md"},n=i('<h1 id="intel®-realsensetm-tracking-camera-t265-vio" tabindex="-1">Intel® RealSense™ Tracking Camera T265 (VIO) <a class="header-anchor" href="#intel®-realsensetm-tracking-camera-t265-vio" aria-label="Permalink to &quot;Intel® RealSense™ Tracking Camera T265 (VIO)&quot;">​</a></h1><p>The <a href="https://www.intelrealsense.com/tracking-camera-t265/" target="_blank" rel="noreferrer">Intel® RealSense™ Tracking Camera T265</a> provides odometry information that can be used for <a href="./../computer_vision/visual_inertial_odometry.html">VIO</a>, augmenting or replacing other positioning systems on PX4.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This camera is recommended, and is used in the <a href="./../computer_vision/visual_inertial_odometry.html#suggested-setup">Visual Inertial Odometry (VIO) &gt; Suggested Setup</a>.</p></div><p><img src="'+e+`" alt="Intel® RealSense™ Tracking Camera T265 - Angled Image"></p><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p><a href="https://www.intelrealsense.com/tracking-camera-t265/" target="_blank" rel="noreferrer">Intel® RealSense™ Tracking Camera T265</a> (store.intelrealsense.com)</p><h2 id="setup-instructions" tabindex="-1">Setup Instructions <a class="header-anchor" href="#setup-instructions" aria-label="Permalink to &quot;Setup Instructions&quot;">​</a></h2><p>At a high level:</p><ul><li>The <a href="https://github.com/IntelRealSense/realsense-ros" target="_blank" rel="noreferrer"><code>realsense-ros</code> wrapper</a> provided by Intel should be used to extract the raw data from the camera.</li><li>The camera should be mounted with lenses facing down (default). Be sure to specify the camera orientation by publishing the static transform between the <code>base_link</code> and <code>camera_pose_frame</code> in a ROS launch file, for example:<div class="language-xml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">xml</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">&lt;</span><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">node</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> pkg</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;tf&quot;</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> type</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;static_transform_publisher&quot;</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> name</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;tf_baseLink_cameraPose&quot;</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">    args</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;0 0 0 0 1.5708 0 base_link camera_pose_frame 1000&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">/&gt;</span></span></code></pre></div>This is a static transform that links the camera ROS frame <code>camera_pose_frame</code> to the MAVROS drone frame <code>base_link</code>. <ul><li>the first three <code>args</code> specify <em>translation</em> x,y,z in metres from the center of the flight controller to the camera. For example, if the camera is 10cm in front of the controller and 4cm up, the first three numbers would be : [0.1, 0, 0.04,...]</li><li>the next three <code>args</code> specify rotation in radians (yaw, pitch, roll). So <code>[... 0, 1.5708, 0]</code> means pitch down by 90° (facing the ground). Facing straight forward would be [... 0 0 0].</li></ul></li><li>The camera is sensitive to high-frequency vibrations! It should be soft-mounted with, for example, vibration isolation foam.</li></ul>`,9),o=[n];function l(h,c,p,d,m,k){return s(),t("div",null,o)}const f=a(r,[["render",l]]);export{g as __pageData,f as default};
