import{_ as t,a,b as s,c as o,d as l,e as n}from"./chunks/crazyflie_sdcard.0v20hxkZ.js";import{_ as r,a as h,b as p,c,d,e as u}from"./chunks/crazyflie_QGCjoystick_setup.CH0sevBa.js";import{_ as g}from"./chunks/crazyflie_battery_setup.DNK9-kaQ.js";import{_ as m,E as k,c as b,J as f,m as e,a as y,ab as C,o as v}from"./chunks/framework.CUflZczI.js";const N=JSON.parse('{"title":"Crazyflie 2.0 (Discontinued)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/complete_vehicles_mc/crazyflie2.md","filePath":"zh/complete_vehicles_mc/crazyflie2.md"}'),F={name:"zh/complete_vehicles_mc/crazyflie2.md"},w=e("h1",{id:"crazyflie-2-0-discontinued",tabindex:"-1"},[y("Crazyflie 2.0 (Discontinued) "),e("a",{class:"header-anchor",href:"#crazyflie-2-0-discontinued","aria-label":'Permalink to "Crazyflie 2.0 (Discontinued)"'},"​")],-1),_=C('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><em>Crazyflie 2.0</em> has been <a href="./../flight_controller/autopilot_experimental.html">discontinued/superseded</a>. Try <a href="./../complete_vehicles_mc/crazyflie21.html">Bitcraze Crazyflie 2.1</a> instead!</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><ul><li>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://www.bitcraze.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</li><li>PX4 support for this flight controller is <a href="./../flight_controller/autopilot_experimental.html">experimental</a>.</li></ul></div><p>The Crazyflie line of micro quads was created by Bitcraze AB. An overview of the Crazyflie 2.0 can be <a href="https://www.bitcraze.io/crazyflie-2/" target="_blank" rel="noreferrer">found here</a>.</p><p><img src="'+t+'" alt="Crazyflie2 Image"></p><h2 id="quick-summary" tabindex="-1">Quick Summary <a class="header-anchor" href="#quick-summary" aria-label="Permalink to &quot;Quick Summary&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The main hardware documentation is here: <a href="https://wiki.bitcraze.io/projects:crazyflie2:index" target="_blank" rel="noreferrer">https://wiki.bitcraze.io/projects:crazyflie2:index</a></p></div><ul><li>Main System-on-Chip: STM32F405RG <ul><li>CPU: 168 MHz ARM Cortex M4 with single-precision FPU</li><li>RAM: 192 KB SRAM</li></ul></li><li>nRF51822 radio and power management MCU</li><li>MPU9250 Accel / Gyro / Mag</li><li>LPS25H barometer</li></ul><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><ul><li><a href="https://store.bitcraze.io/collections/kits/products/crazyflie-2-0" target="_blank" rel="noreferrer">Crazyflie 2.0</a>.</li><li><a href="https://store.bitcraze.io/collections/kits/products/crazyradio-pa" target="_blank" rel="noreferrer">Crazyradio PA 2.4 GHz USB dongle</a>: used for wireless communication between <em>QGroundControl</em> and Crazyflie 2.0.</li><li><a href="https://store.bitcraze.io/collections/decks/products/breakout-deck" target="_blank" rel="noreferrer">Breakout deck</a>: breakout expansion board for connecting new peripherals.</li><li><a href="https://store.bitcraze.io/collections/decks/products/flow-deck" target="_blank" rel="noreferrer">Flow deck</a>: contains an optical flow sensor to measure movements of the ground and a distance sensor to measure the distance to the ground. This will be useful for precise altitude and position control.</li><li><a href="https://store.bitcraze.io/collections/decks/products/z-ranger-deck" target="_blank" rel="noreferrer">Z-ranger deck</a> has the same distance sensor as the Flow deck to measure the distance to the ground. This will be useful for precise altitude control.</li><li><a href="https://store.bitcraze.io/collections/decks/products/sd-card-deck" target="_blank" rel="noreferrer">SD-card deck</a>: used for high speed onboard logging to a micro SD card.</li><li><a href="https://support.logi.com/hc/en-us/articles/360024326793--Getting-Started-Gamepad-F310" target="_blank" rel="noreferrer">Logitech Joystick</a>.</li></ul><h2 id="flashing-px4" tabindex="-1">Flashing PX4 <a class="header-anchor" href="#flashing-px4" aria-label="Permalink to &quot;Flashing PX4&quot;">​</a></h2><p>After setting up the PX4 development environment, follow these steps to install the PX4 Autopilot on the Crazyflie 2.0:</p><ol><li><p>Download the source code of the PX4 Bootloader:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> clone</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://github.com/PX4/Bootloader.git</span></span></code></pre></div></li><li><p>Navigate into the top directory of the source code and compile it using:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> crazyflie_bl</span></span></code></pre></div></li><li><p>Put the Crazyflie 2.0 into DFU mode by following these steps:</p><ul><li>Ensure it is initially unpowered.</li><li>Hold down the reset button (see figure below...). <img src="'+r+`" alt="Crazyflie2 Reset Button"></li><li>Plug into computer&#39;s USB port.</li><li>After a second, the blue LED should start blinking and after 5 seconds should start blinking faster.</li><li>Release button.</li></ul></li><li><p>Install <em>dfu-util</em>:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> update</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dfu-util</span></span></code></pre></div></li><li><p>Flash bootloader using <em>dfu-util</em> and unplug Crazyflie 2.0 when done:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dfu-util</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -d</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> 0483:df11</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -a</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 0</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -s</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 0x08000000</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -D</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./build/crazyflie_bl/crazyflie_bl.bin</span></span></code></pre></div><p>When powering on the Crazyflie 2.0 the yellow LED should blink.</p></li><li><p>Download the source code of the PX4 autopilot:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> clone</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://github.com/PX4/PX4-Autopilot.git</span></span></code></pre></div></li><li><p>Navigate into the top directory of the source code and compile it using:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> bitcraze_crazyflie_default</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> upload</span></span></code></pre></div></li><li><p>When prompted to plug in device, plug in Crazyflie 2.0. The yellow LED should start blinking indicating bootloader mode. Then the red LED should turn on indicating that the flashing process has started.</p></li><li><p>Wait for completion.</p></li><li><p>Done! Calibrate the sensors using <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/sensors.html" target="_blank" rel="noreferrer">QGroundControl</a>.</p></li></ol><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If QGroundControl does not connect with the vehicle, ensure that in <a href="https://github.com/PX4/PX4-Autopilot/blob/release/1.15/boards/bitcraze/crazyflie/nuttx-config/nsh/defconfig" target="_blank" rel="noreferrer">nuttx-config</a> for crazyflie <code># CONFIG_DEV_LOWCONSOLE is not set</code> is replaced by <code>CONFIG_DEV_LOWCONSOLE=y</code>. This should be done using <em>menuconfig</em>:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> bitcraze_crazyflie_default</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> menuconfig</span></span></code></pre></div><p>or <em>qconfig</em> (Check <em>Low-level console support</em> under <em>Serial Driver Support</em> in GUI):</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> bitcraze_crazyflie_default</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> qconfig</span></span></code></pre></div></div><h2 id="wireless-setup-instructions" tabindex="-1">Wireless Setup Instructions <a class="header-anchor" href="#wireless-setup-instructions" aria-label="Permalink to &quot;Wireless Setup Instructions&quot;">​</a></h2><p>The onboard nRF module allows connecting to the board via Bluetooth or through the proprietary 2.4GHz Nordic ESB protocol.</p><ul><li>A <a href="https://www.bitcraze.io/crazyradio-pa/" target="_blank" rel="noreferrer">Crazyradio PA</a> is recommended.</li><li>To fly the Crazyflie 2.0 right away, the Crazyflie phone app is supported via Bluetooth.</li></ul><p>Using the official Bitcraze <strong>Crazyflie phone app</strong>:</p><ul><li>Connect via Bluetooth.</li><li>Change mode in settings to 1 or 2.</li><li>Calibrate via QGroundControl.</li></ul><p>Connecting via <strong>MAVLink</strong>:</p><ul><li><p>Use a Crazyradio PA alongside a compatible GCS.</p></li><li><p>Download the <em>crazyflie-lib-python</em> source code:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> clone</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://github.com/bitcraze/crazyflie-lib-python.git</span></span></code></pre></div></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>We will use <a href="https://github.com/bitcraze/crazyflie-lib-python/blob/master/examples/cfbridge.py" target="_blank" rel="noreferrer">cfbridge.py</a> to setup a wireless MAVlink communication link between Crazyflie 2.0 (flashed with PX4) and QGroundControl. <em>Cfbridge</em> enables QGroundControl to communicate with the crazyradio PA. The <a href="https://github.com/dennisss/cfbridge" target="_blank" rel="noreferrer">C based cfbridge</a> is currently experiencing data loss issues, which is why we have chosen to use <strong>cfbridge.py</strong>.</p></div><ul><li><p>Make sure you have set the udev permissions to use the USB Radio. To do this, follow the steps listed <a href="https://www.bitcraze.io/documentation/repository/crazyflie-lib-python/master/installation/usb_permissions/" target="_blank" rel="noreferrer">here</a> and <strong>restart</strong> your computer.</p></li><li><p>Connect a Crazyradio PA via USB.</p></li><li><p>Build a <a href="https://virtualenv.pypa.io/en/latest/" target="_blank" rel="noreferrer">virtual environment (local python environment)</a> with package dependencies using the following method:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">pip</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> tox</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --user</span></span></code></pre></div></li><li><p>Navigate to the crazyflie-lib-python folder and type:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> venv</span></span></code></pre></div></li><li><p>Activate the virtual environment:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">source</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> venv-cflib/bin/activate</span></span></code></pre></div></li><li><p>Install required dependencies:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">pip</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -r</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> requirements.txt</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --user</span></span></code></pre></div></li></ul><p>To connect Crazyflie 2.0 with crazyradio, <strong>launch cfbridge</strong> by following these steps:</p><ul><li><p>Power off and power on Crazyflie 2.0 and wait for it to boot up.</p></li><li><p>Connect a Crazyflie radio device via USB.</p></li><li><p>Navigate to the crazyflie-lib-python folder.</p></li><li><p>Activate the environment:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">source</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> venv-cflib/bin/activate</span></span></code></pre></div></li><li><p>Navigate to the examples folder:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">cd</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> examples</span></span></code></pre></div></li><li><p>Launch cfbridge:</p></li></ul><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">python</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> cfbridge.py</span></span></code></pre></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>Cfbridge</em> by default tries to initiate the radio link communication on channel 80 and with crazyflie address 0xE7E7E7E7E7. If you are using <a href="https://github.com/dennisss/cfbridge/blob/master/index.md#advanced-swarming" target="_blank" rel="noreferrer">multiple crazyflies and/or crazyradios</a> in the same room and want to use a different channel and/or address for each, first connect the crazyflie with QGroundControl via a USB cable and change the syslink parameters (channel, address) in QGroundControl. Next, launch the cfbridge by giving the same channel and address as the first and second arguments respectively, e.g: <code>python cfbridge.py 90 0x0202020202</code></p></div><ul><li>Open QGroundControl.</li><li>After using <em>cfbridge</em>, you can deactivate the virtualenv if you activated it by pressing <code>CTRL+z</code>. Most of the time, launching <em>cfbridge</em> again from the same terminal doesn&#39;t connect to crazyflie, this can be solved by closing the terminal and relaunching <em>cfbridge</em> in a new terminal.</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>If you change any driver in <a href="https://github.com/bitcraze/crazyflie-lib-python" target="_blank" rel="noreferrer">crazyflie-lib-python</a> or if launching <em>cfbridge</em> in a new terminal does not find crazyflie, you can try navigating to the crazyflie-lib-python folder and run the script below to rebuild cflib.</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> venv</span></span></code></pre></div></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>To use Joystick, set <code>COM_RC_IN_MODE</code> in QGroundControl to &quot;Joystick/No RC Checks&quot;. Calibrate the Joystick and set the Joystick message frequency in QGroundControl to any value between 5 to 14 Hz (10 Hz is recommended). To be able to set the frequency, the advanced option should be enabled. This is the rate at which Joystick commands are sent from QGroundControl to Crazyflie 2.0 (to do this, you will need to follow the instructions <a href="https://github.com/mavlink/qgroundcontrol" target="_blank" rel="noreferrer">here</a> to obtain the latest QGroundControl source code (master) and build it).</p></div><p><img src="`+a+'" alt=""></p><h2 id="hardware-setup" tabindex="-1">Hardware Setup <a class="header-anchor" href="#hardware-setup" aria-label="Permalink to &quot;Hardware Setup&quot;">​</a></h2><p>Crazyflie 2.0 is able to fly with precise control in <a href="./../flight_modes_mc/manual_stabilized.html">Stabilized mode</a>, <a href="./../flight_modes_mc/altitude.html">Altitude mode</a> and <a href="./../flight_modes_mc/position.html">Position mode</a>.</p><ul><li>You will need the <a href="https://store.bitcraze.io/collections/decks/products/z-ranger-deck" target="_blank" rel="noreferrer">Z-ranger deck</a> to fly in <em>Altitude</em> mode. If you also want to fly in the <em>Position</em> mode, it is recommended you buy the <a href="https://store.bitcraze.io/collections/decks/products/flow-deck" target="_blank" rel="noreferrer">Flow deck</a> which also has the integrated Z-ranger sensor.</li><li>The onboard barometer is highly susceptible to any external wind disturbances including those created by Crazyflie&#39;s own propellers. Hence, we isolated the barometer with a piece of foam, and then mounted the distance sensor on top of it as shown below:</li></ul><p><img src="'+s+'" alt="Crazyflie barometer"></p><p><img src="'+o+'" alt="Crazyflie barometer foam"></p><p><img src="'+l+'" alt="Crazyflie optical flow"></p><p>In order to log flight details, you can mount SD card deck on top of crazyflie as shown below:</p><p><img src="'+n+'" alt="Crazyflie SDCard"></p><p>Then, you need to stick the battery on top of the SD card deck using a double sided tape:</p><p><img src="'+g+'" alt="Crazyflie battery setup"></p><h2 id="altitude-control" tabindex="-1">Altitude Control <a class="header-anchor" href="#altitude-control" aria-label="Permalink to &quot;Altitude Control&quot;">​</a></h2><p>Crazyflie is able to fly in <em>Altitude</em> mode if you use a <a href="https://store.bitcraze.io/collections/decks/products/z-ranger-deck" target="_blank" rel="noreferrer">Z-ranger deck</a>. According to the datasheet, the maximum height (above ground) the range finder can sense is 2 m. However, when tested on dark surfaces this value decreases to 0.5 m. On a light floor, it goes up to max 1.3 m. This means you cannot hold altitudes above this value in <em>Altitude</em> or <em>Position</em> flight modes.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>If the Crazyflie 2.0 height drifts at mid-throttle command in <em>Altitude mode</em> or <em>Position mode</em>, first try rebooting the vehicle. If this does not fix the problem, recalibrate the accel and mag (compass).\\</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Since the onboard barometer is highly susceptible to wind disturbances created by the Crazyflie&#39;s own propellers, you cannot rely on it to hold altitude.</p></div><h2 id="position-control" tabindex="-1">Position Control <a class="header-anchor" href="#position-control" aria-label="Permalink to &quot;Position Control&quot;">​</a></h2><p>With <a href="https://store.bitcraze.io/collections/decks/products/flow-deck" target="_blank" rel="noreferrer">Flow deck</a>, you can fly Crazyflie 2.0 in <em>Position mode</em>. Unlike <a href="./../sensor/px4flow.html">PX4FLOW</a>, the flow deck does not house a gyro, hence the onboard gyro is used for flow fusion to find the local position estimates. Moreover, the flow deck shares the same SPI bus as the SD card deck, therefore logging at high rate on SD card is not recommended when flying in <em>Position mode</em>.</p><h2 id="using-frsky-taranis-rc-transmitter-as-joystick" tabindex="-1">Using FrSky Taranis RC Transmitter as Joystick <a class="header-anchor" href="#using-frsky-taranis-rc-transmitter-as-joystick" aria-label="Permalink to &quot;Using FrSky Taranis RC Transmitter as Joystick&quot;">​</a></h2><p>If you already own a Taranis RC transmitter and want to use it as a controller, it can be configured as a USB Joystick:</p><ul><li><p>Create a new model in Taranis.</p><p><img src="'+h+'" alt="Taranis - new model"></p></li><li><p>In <em>MODEL SETUP</em> menu page, turn off both internal and external TX modules.</p><p><img src="'+p+'" alt="Taranis - model setup"></p></li><li><p>In <em>OUTPUTS</em> menu page (also called “SERVOS” page in some Taranis transmitters), invert Throttle (CH1) and Aileron (CH3).</p><p><img src="'+c+'" alt="Taranis - outputs"></p></li></ul><p>To use Taranis switches to arm/disarm and switch to different flight modes:</p><ul><li><p>In Taranis UI <em>MIXER</em> menu page, you can assign the switches to any channel in the range channel 9-16 which map to the buttons 0-7 in the QGroundControl Joystick setup. For example, Taranis “SD” switch can be set to channel 9 in Taranis UI:</p><p><img src="'+d+'" alt="Taranis switch setup"></p></li><li><p>Connect Taranis to PC with a USB cable and Open QGroundControl.</p></li><li><p>In QGroundControl Joystick Setup, you can see the buttons turning yellow when you switch them on. For example, channel 9 in Taranis maps to button 0 in QGroundControl Joystick setup. You can assign any mode to this button e.g. <em>Altitude</em> mode. Now when you lower the switch &quot;SD&quot;, flight mode will change to <em>Altitude</em>.</p><p><img src="'+u+'" alt="Joystick setup"></p></li></ul><h3 id="ros" tabindex="-1">ROS <a class="header-anchor" href="#ros" aria-label="Permalink to &quot;ROS&quot;">​</a></h3><p>To connect to Crazyflie 2.0 via MAVROS:</p><ul><li><p>Start up <em>cfbridge</em> using the above instructions.</p></li><li><p>Change the UDP port QGroundControl listens to:</p><ul><li>In QGroundControl, navigate to <strong>Application Settings &gt; General</strong> and uncheck all the boxes under <em>Autoconnect to the following devices</em>.</li><li>Add in <strong>Comm Links</strong> a link of type <em>UDP</em>, check the <em>Automatically Connect on Start</em> option, change the <em>Listening Port</em> to 14557, add Target Hosts: 127.0.0.1 and then press <strong>OK</strong>.</li></ul></li><li><p>Make sure you have <a href="https://github.com/mavlink/mavros/tree/master/mavros#installation" target="_blank" rel="noreferrer">MAVROS</a> installed.</p></li><li><p>Start MAVROS with command:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">roslaunch</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> mavros</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> px4.launch</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> fcu_url:=&quot;udp://:14550@127.0.0.1:14551&quot;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> gcs_url:=&quot;udp://@127.0.0.1:14557&quot;</span></span></code></pre></div></li><li><p>Restart QGroundControl if it doesn&#39;t connect.</p></li></ul><h2 id="flying" tabindex="-1">Flying <a class="header-anchor" href="#flying" aria-label="Permalink to &quot;Flying&quot;">​</a></h2>',55),z=e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/2Bcy3k1h5uc",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1);function B(P,E,S,T,x,A){const i=k("Badge");return v(),b("div",null,[w,f(i,{type:"info",text:"Discontinued"}),_,z])}const O=m(F,[["render",B]]);export{N as __pageData,O as default};
