import{_ as e,c as i,o as a,ab as o}from"./chunks/framework.CUflZczI.js";const u=JSON.parse('{"title":"计算机视觉 (光流，MoCap， VIO，避障)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/computer_vision/index.md","filePath":"zh/computer_vision/index.md"}'),t={name:"zh/computer_vision/index.md"},r=o('<h1 id="计算机视觉-光流-mocap-vio-避障" tabindex="-1">计算机视觉 (光流，MoCap， VIO，避障) <a class="header-anchor" href="#计算机视觉-光流-mocap-vio-避障" aria-label="Permalink to &quot;计算机视觉 (光流，MoCap， VIO，避障)&quot;">​</a></h1><p><a href="https://en.wikipedia.org/wiki/Computer_vision" target="_blank" rel="noreferrer">计算机视觉</a> 技术使计算机能够使用视觉数据来理解他们的环境。</p><p>PX4使用计算机视觉系统(主要在<a href="./../companion_computer/README.html">机载计算机</a>上运行)以支持下列功能：</p><ul><li>Pose/Velocity Estimation: <ul><li><a href="./../sensor/optical_flow.html">光流</a>提供2D速度估计（使用向下的相机和向下的距离传感器）。</li><li><a href="./../computer_vision/motion_capture.html">Motion Capture</a> provides 3D pose estimation using a vision system that is <em>external</em> to the vehicle. It is primarily used for indoor navigation.</li><li><a href="./../computer_vision/visual_inertial_odometry.html">Visual Inertial Odometry (VIO)</a> provides 3D pose and velocity estimation using an onboard vision system and IMU. It is used for navigation when global position information is absent or unreliable.</li></ul></li><li>Avoidance/Path Planning: <ul><li><a href="./../computer_vision/obstacle_avoidance.html">自主避障</a> 在飞行规划好的路径时提供完全的避障导航（支持当前任务）。 这依赖机载电脑上运行的 <a href="https://github.com/PX4/PX4-Avoidance" target="_blank" rel="noreferrer">PX4/avoidance</a>。</li><li><a href="./../computer_vision/collision_prevention.html">碰撞预防</a>使载具在撞到障碍物之前停止（主要是在手动模式下飞行时）。</li><li><a href="./../computer_vision/safe_landing.html">安全着陆</a> 引导无人机找到（并着陆）没有固定障碍物的平坦地形。</li></ul></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../complete_vehicles/px4_vision_kit.html">PX4 视觉开发套件</a> （Holybro）是一款功能强大且价格便宜的套件，适用于在 PX4 上使用计算机视觉的开发人员。 It comes with no pre-installed software, but does include an example implementation of obstacle avoidance to demonstrate the capabilities of the platform.</p></div><h2 id="外部资源" tabindex="-1">外部资源 <a class="header-anchor" href="#外部资源" aria-label="Permalink to &quot;外部资源&quot;">​</a></h2><ul><li><a href="https://github.com/robin-shaun/XTDrone/blob/master/README.en.md" target="_blank" rel="noreferrer">XTDrone</a> - ROS + PX4 v1.9 计算机视觉模拟仿真环境。 <a href="https://www.yuque.com/xtdrone/manual_en" target="_blank" rel="noreferrer">XTDrone 手册</a> 有你需要开始的一切！</li></ul>',7),n=[r];function l(s,c,m,p,h,_){return a(),i("div",null,n)}const f=e(t,[["render",l]]);export{u as __pageData,f as default};
