import{_ as e,c as a,o as t,ab as o}from"./chunks/framework.CUflZczI.js";const d=JSON.parse('{"title":"运动捕捉（MoCap）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/computer_vision/motion_capture.md","filePath":"zh/computer_vision/motion_capture.md"}'),i={name:"zh/computer_vision/motion_capture.md"},r=o('<h1 id="运动捕捉-mocap" tabindex="-1">运动捕捉（MoCap） <a class="header-anchor" href="#运动捕捉-mocap" aria-label="Permalink to &quot;运动捕捉（MoCap）&quot;">​</a></h1><p>运动捕捉（MoCap）是一种 <a href="https://en.wikipedia.org/wiki/Computer_vision" target="_blank" rel="noreferrer">计算机视觉</a> 技术， 用于使用 <em>外部</em>定位机制估算无人机 3D <em>姿态</em> （位置和方向）。 它通常用于无GPS的情况下进行无人机的导航(例如室内)，并提供在<em>本地坐标系</em>下的位置信息。</p><p><em>MoCap</em> 系统最常使用红外相机检测运动，但也可以使用其他类型的相机，激光雷达或者超宽带 （UWB）。</p><p>:::note <em>MoCap</em> 在概念上类似于 <a href="./../computer_vision/visual_inertial_odometry.html">视觉惯性里程计 (VIO)</a>。 主要区别在于，在 VIO 中，视觉系统运行在无人机上。 额外还需要利用无人机上 IMU 提供速度信息。 :::</p><h2 id="mocap-资源" tabindex="-1">MoCap 资源 <a class="header-anchor" href="#mocap-资源" aria-label="Permalink to &quot;MoCap 资源&quot;">​</a></h2><p>有关 MoCap 的信息，请参阅：</p><ul><li><a href="./../ros/external_position_estimation.html">使用视觉或运动捕捉系统进行位置估计</a>。 </li><li><a href="./../tutorials/motion-capture.html">利用动作捕捉系统进行飞行 （VICON，Optitrack）</a>。 </li><li><a href="./../advanced_config/tuning_the_ecl_ekf.html#external-vision-system">EKF &gt; 外部视觉系统</a></li></ul>',7),n=[r];function p(_,m,c,s,l,h){return t(),a("div",null,n)}const f=e(i,[["render",p]]);export{d as __pageData,f as default};
