import{_ as e}from"./chunks/micronav.BndX9JiN.js";import{_ as t,c as o,o as r,ab as a}from"./chunks/framework.CUflZczI.js";const k=JSON.parse('{"title":"操纵杆设置","description":"","frontmatter":{},"headers":[],"relativePath":"zh/config/joystick.md","filePath":"zh/config/joystick.md"}'),n={name:"zh/config/joystick.md"},i=a('<h1 id="操纵杆设置" tabindex="-1">操纵杆设置 <a class="header-anchor" href="#操纵杆设置" aria-label="Permalink to &quot;操纵杆设置&quot;">​</a></h1><p>A <a href="https://en.wikipedia.org/wiki/Joystick" target="_blank" rel="noreferrer">computer joystick</a> or gamepad connected through <em>QGroundControl</em> can be used to manually control the vehicle (<em>instead</em> of using an <a href="./../config/radio.html">RC Transmitter</a>).</p><p>This approach may be used by manual control units that have an integrated ground control station (like the <em>UAVComponents</em> <a href="https://uxvtechnologies.com/ground-control-stations/micronav/" target="_blank" rel="noreferrer">MicroNav</a> shown below). Joysticks are also commonly used to allow developers to fly the vehicle in simulation.</p><p><img src="'+e+'" alt="Joystick MicroNav"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./../config/radio.html">Radio Setup</a> is not required if using only a joystick (because a joystick is not an RC controller)!</p></div><div class="info custom-block"><p class="custom-block-title"><em>QGroundControl</em> uses the cross-platform <a href="http://www.libsdl.org/index.php" target="_blank" rel="noreferrer">SDL2</a> library to convert joystick movements to MAVLink <a href="https://mavlink.io/en/messages/common.html#MANUAL_CONTROL" target="_blank" rel="noreferrer">MANUAL_CONTROL</a> messages, which are then sent to PX4 over the telemetry channel. In consequence, a joystick-based controller system requires a reliable high bandwidth telemetry channel to ensure that the vehicle is responsive to joystick movements.</p></div><h2 id="enabling-px4-joystick-support" tabindex="-1">Enabling PX4 Joystick Support <a class="header-anchor" href="#enabling-px4-joystick-support" aria-label="Permalink to &quot;Enabling PX4 Joystick Support&quot;">​</a></h2><p>Information about how to set up a joystick is covered in: <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/joystick.html" target="_blank" rel="noreferrer">QGroundControl &gt; Joystick Setup</a>.</p><p>In summary:</p><ul><li>Open <em>QGroundControl</em></li><li>Set the parameter <a href="./../advanced_config/parameter_reference.html#COM_RC_IN_MODE">COM_RC_IN_MODE=1</a> - <code>Joystick</code><ul><li>See <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/parameters.html" target="_blank" rel="noreferrer">Parameters</a> for information about setting parameters</li><li>Setting the parameter to <code>2</code> or <code>3</code> also enables Joystick under some circumstances.</li></ul></li><li>Connect the joystick</li><li>Configure the connected joystick in: <strong>Vehicle Setup &gt; Joystick</strong>.</li></ul>',10),s=[i];function c(l,m,h,p,d,u){return r(),o("div",null,s)}const f=t(n,[["render",c]]);export{k as __pageData,f as default};
