import{_ as e}from"./chunks/arch-gazebo.C3Qru3Pm.js";import{_ as s,c as t,o as a,ab as n}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"ArchLinux 上的开发环境","description":"","frontmatter":{},"headers":[],"relativePath":"zh/dev_setup/dev_env_linux_arch.md","filePath":"zh/dev_setup/dev_env_linux_arch.md"}'),o={name:"zh/dev_setup/dev_env_linux_arch.md"},i=n('<h1 id="archlinux-上的开发环境" tabindex="-1">ArchLinux 上的开发环境 <a class="header-anchor" href="#archlinux-上的开发环境" aria-label="Permalink to &quot;ArchLinux 上的开发环境&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>This development environment is <a href="./../advanced/community_supported_dev_env.html">community supported and maintained</a>. It may or may not work with current versions of PX4.</p><p>See <a href="./../dev_setup/dev_env.html">Toolchain Installation</a> for information about the environments and tools supported by the core development team.</p></div><p>The PX4-Autopilot repository provides a convenient script to set your Arch installation up for PX4 development: <a href="https://github.com/PX4/PX4-Autopilot/blob/release/1.15/Tools/setup/arch.sh" target="_blank" rel="noreferrer">Tools/setup/arch.sh</a>. </p><p>The script installs (by default) all tools to build PX4 for NuttX targets and run simulation with <a href="./../sim_jmavsim/">JMAVSim</a>. You can additionally install the <a href="./../sim_gazebo_classic/">Gazebo Classic</a> simulator by specifying the command line argument: <code>--gazebo</code>.</p><p><img src="'+e+`" alt="Gazebo on Arch"></p><div class="info custom-block"><p class="custom-block-title">The instructions have been tested on <a href="https://manjaro.org/" target="_blank" rel="noreferrer">Manjaro</a> (Arch based distribution) as it is much easier to set up than Arch Linux.</p></div><p>To get and run the scripts, do either of:</p><ul><li><p><a href="./../dev_setup/building_px4.html">Download PX4 Source Code</a> and run the scripts in place:</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">wget</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://raw.githubusercontent.com/PX4/containers/master/docker/px4-dev/scripts/archlinux_install_script.sh</span></span></code></pre></div></li></ul><p>sudo -s source ./archlinux_install_script.sh</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span></span></span>
<span class="line"><span>- 只下载你所需的脚本并运行他们：</span></span>
<span class="line"><span></span></span>
<span class="line"><span>\`\`\`sh</span></span>
<span class="line"><span>wget https://raw.githubusercontent.com/PX4/PX4-Autopilot/release/1.15/Tools/setup/arch.sh</span></span>
<span class="line"><span>wget https://raw.githubusercontent.com/PX4/PX4-Autopilot/release/1.15/Tools/setup/requirements.txt</span></span>
<span class="line"><span>bash arch.sh</span></span></code></pre></div><p>The script takes the following optional parameters:</p><ul><li><p><code>--gazebo</code>: Add this parameter to install Gazebo from the <a href="https://aur.archlinux.org/packages/gazebo/" target="_blank" rel="noreferrer">AUR</a>.</p><div class="info custom-block"><p class="custom-block-title">Gazebo gets compiled from source. It takes some time to install and requires entering the <code>sudo</code> password multiple times (for dependencies).</p></div></li></ul><p>:::</p><ul><li><code>--no-nuttx</code>: Do not install the NuttX/Pixhawk toolchain (i.e. if only using simulation).</li><li><code>--no-sim-tools</code>：不要安装 jMAVSim/Gazebo 仿真器（例如你只想使用或者开发调试 Pixhawk/NuttX）</li></ul>`,14),l=[i];function r(p,c,h,u,d,m){return a(),t("div",null,l)}const g=s(o,[["render",r]]);export{b as __pageData,g as default};
