import{_ as t,a as e,b as a,c as r,d as i,e as n}from"./chunks/hardware-mindpx-pin.Dshw6srs.js";import{_ as o,c as l,o as d,ab as s}from"./chunks/framework.CUflZczI.js";const M=JSON.parse('{"title":"MindPX Hardware","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/mindpx.md","filePath":"zh/flight_controller/mindpx.md"}'),c={name:"zh/flight_controller/mindpx.md"},p=s('<h1 id="mindpx-hardware" tabindex="-1">MindPX Hardware <a class="header-anchor" href="#mindpx-hardware" aria-label="Permalink to &quot;MindPX Hardware&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 does not manufacture this (or any) autopilot. Contact the <a href="http://mindpx.net" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The AirMind<sup>®</sup> <a href="http://mindpx.net" target="_blank" rel="noreferrer">MindPX</a> series is a new generation autopilot system branched from Pixhawk<sup>®</sup>.</p><p><img src="'+t+'" alt="MindPX Controller"></p><div class="info custom-block"><p class="custom-block-title">These flight controllers are <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="总览" tabindex="-1">总览 <a class="header-anchor" href="#总览" aria-label="Permalink to &quot;总览&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">The main hardware documentation is <a href="http://mindpx.net/assets/accessories/Specification9.18_3_pdf.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><p>MindPX is a new generation autopilot system branched from Pixhawk<sup>®</sup>, been revised in schematic and structure, and been further enhanced with new features to make un-manned vehicle more smart and more friendly to use.</p><p>MindPX increases total PWM output channels to 16 (8 main outputs + 8 aux outputs). This means that MindPX can support more complicated VTOL configurations and more fine control. It is especially meaningful for those FMU-V4 based flight controllers as MindPX implements main and aux output in one single FMU.</p><p><img src="'+e+'" alt=""></p><ul><li><p>Main System-on-Chip: STM32F427</p><ul><li>CPU: 32bits, 168 MHz ARM Cortex<sup>®</sup> M4 with FPU</li><li>RAM: 256 KB SRAM</li><li>2MB Flash</li><li>ST Micro LSM303D 14 bit accelerometer/magnetometer</li><li>MEAS MS5611 气压计</li><li>InvenSense<sup>®</sup> MPU6500 integrated 6-axis sensors</li></ul></li><li><p>Highlighted features:</p><ul><li>CNC processed aluminum alloy case, light and solid</li><li>Built-in isolated IMU redundancy</li><li>Total 16 PWM output channel (8 main + 8 aux)</li><li>1 extra I2C port for flow connection.</li><li>1 extra USB port for companion computer connection (built-in UART-to-USB converter)</li><li>Exposed debug port for development</li></ul></li></ul><h2 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h2><h3 id="mounting" tabindex="-1">Mounting <a class="header-anchor" href="#mounting" aria-label="Permalink to &quot;Mounting&quot;">​</a></h3><p><img src="'+a+'" alt="MindPX Mounting"></p><h3 id="布线" tabindex="-1">布线 <a class="header-anchor" href="#布线" aria-label="Permalink to &quot;布线&quot;">​</a></h3><p><img src="'+r+'" alt="MindPX Wiring 1"></p><p><img src="'+i+'" alt="MindPX Wiring 2"></p><h3 id="针脚" tabindex="-1">针脚 <a class="header-anchor" href="#针脚" aria-label="Permalink to &quot;针脚&quot;">​</a></h3><p><img src="'+n+'" alt="MindPX Pinout"></p><table><thead><tr><th style="text-align:center;">Num.</th><th style="text-align:center;">描述</th><th style="text-align:center;">Num.</th><th style="text-align:center;">描述</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;">电源</td><td style="text-align:center;">9</td><td style="text-align:center;">I2C2 (MindFLow)</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;">Debug (refresh bootloader)</td><td style="text-align:center;">10</td><td style="text-align:center;">USB2 (Serial 2 to USB)</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;">USB1 (refresh firmware)</td><td style="text-align:center;">11</td><td style="text-align:center;">UART4,5</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;">Reset</td><td style="text-align:center;">12</td><td style="text-align:center;">UART1 (Telemetry)</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;">UART3 (GPS)</td><td style="text-align:center;">13</td><td style="text-align:center;">CAN</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:center;">I2C1(external compass)</td><td style="text-align:center;">14</td><td style="text-align:center;">ADC</td></tr><tr><td style="text-align:center;">7</td><td style="text-align:center;">TF card slot</td><td style="text-align:center;">15</td><td style="text-align:center;">Tricolor Light</td></tr><tr><td style="text-align:center;">8</td><td style="text-align:center;">NRF/SPI(Remote Control)</td><td style="text-align:center;">16</td><td style="text-align:center;">Looper</td></tr></tbody></table><h3 id="radio-receiver" tabindex="-1">Radio Receiver <a class="header-anchor" href="#radio-receiver" aria-label="Permalink to &quot;Radio Receiver&quot;">​</a></h3><p>MindPX supports a wide variety of radio receivers (since V2.6) including: PPM/SBUS/DSM/DSM2/DSMX. MindPX also support FrSky<sup>®</sup> bi-direction telemetry D and S.Port.</p><p>For detailed Pin diagram, please refer to the <a href="http://mindpx.net/assets/accessories/UserGuide9.18_2_pdf.pdf" target="_blank" rel="noreferrer">User Guide</a>.</p><h3 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make airmind_mindpx-v2_default</span></span></code></pre></div><h3 id="companion-pc-connection" tabindex="-1">Companion PC connection <a class="header-anchor" href="#companion-pc-connection" aria-label="Permalink to &quot;Companion PC connection&quot;">​</a></h3><p>MindPX has a USB-TO-UART Bridge IC on the board. A micro-USB to USB type A cable is used for the connection. Connect micro-USB end to the &#39;OBC&#39; port of MindPX and USB type A end to companion computer.</p><p>And the max BAUD rate is the same with px4 family, which is up to 921600.</p><h2 id="user-guide" tabindex="-1">User Guide <a class="header-anchor" href="#user-guide" aria-label="Permalink to &quot;User Guide&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">The user guide is <a href="http://mindpx.net/assets/accessories/UserGuide9.18_2_pdf.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="在哪里买" tabindex="-1">在哪里买 <a class="header-anchor" href="#在哪里买" aria-label="Permalink to &quot;在哪里买&quot;">​</a></h2><p>MindRacer is available at <a href="http://drupal.xitronet.com/?q=catalog" target="_blank" rel="noreferrer">AirMind Store</a> on internet. You can also find MindRacer at Amazon<sup>®</sup> or eBay<sup>®</sup>.</p><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>RC</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>?</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>Debug Console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>?</td></tr></tbody></table><h2 id="技术支持" tabindex="-1">技术支持 <a class="header-anchor" href="#技术支持" aria-label="Permalink to &quot;技术支持&quot;">​</a></h2><p>Please visit <a href="http://www.mindpx.org" target="_blank" rel="noreferrer">http://www.mindpx.org</a> for more information. Or you can send email to <a href="mailto:support@mindpx.net" target="_blank" rel="noreferrer">support@mindpx.net</a> for any inquiries or help.</p>',38),h=[p];function u(m,g,f,x,b,y){return d(),l("div",null,h)}const S=o(c,[["render",u]]);export{M as __pageData,S as default};
