import{_ as t,a as e,b as a}from"./chunks/hardware-mindracer-conn.CBxfbfEa.js";import{_ as r,c as i,o as n,ab as o}from"./chunks/framework.CUflZczI.js";const x=JSON.parse('{"title":"MindRacer Hardware","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/mindracer.md","filePath":"zh/flight_controller/mindracer.md"}'),l={name:"zh/flight_controller/mindracer.md"},s=o('<h1 id="mindracer-hardware" tabindex="-1">MindRacer Hardware <a class="header-anchor" href="#mindracer-hardware" aria-label="Permalink to &quot;MindRacer Hardware&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 does not manufacture this (or any) autopilot. Contact the <a href="http://mindpx.net" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The AirMind<sup>®</sup> <a href="http://mindpx.net" target="_blank" rel="noreferrer">MindRacer</a> series is a fully stackable flight <em>platform</em> for miniature UAVs. The platform currently has two RTF vehicles: <a href="./../complete_vehicles_mc/mindracer210.html">MindRacer 210</a> and <a href="./../complete_vehicles_mc/nanomind110.html">NanoMind 110</a>.</p><p><img src="'+t+'" alt="MindRacer"></p><div class="info custom-block"><p class="custom-block-title">This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="quick-summary" tabindex="-1">Quick Summary <a class="header-anchor" href="#quick-summary" aria-label="Permalink to &quot;Quick Summary&quot;">​</a></h2><p>MindRacer is a fully stackable flight platform for miniature UAVs. Based on <a href="./../flight_controller/mindpx.html">MindPX</a>, <em>MindRacer</em> further scales down in formfactor while focused on providing modularity. MindRacer is a <em>platform</em> rather than a flight controller.</p><p>MindRacer implements the SEP (soldering-elimination-port) and WEP (wiring-elimination-protocol) concepts. Before SEP and WEP, soldering and wiring are always the major pain and efficiency killer during UAV manufacturing and tuning.</p><div class="info custom-block"><p class="custom-block-title">The main hardware documentation is <a href="http://mindpx.net/assets/accessories/mindracer_spec_v1.2.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><ul><li>Ultra mini size, weight ~6g</li><li>High performance STM32F427 168MHz floating point processor, super fast throttle response</li><li>Support OneShot ESC</li><li>Support PPM/SBUS/DSM radio receivers, support D.Port/S.Port/Wifi telemetry</li><li>On board flight data recorder</li><li>Support IMU isolation</li><li>DroneCode<sup>®</sup> standard compliance connector</li></ul><table><thead><tr><th style="text-align:center;">Item</th><th style="text-align:center;">Description</th></tr></thead><tbody><tr><td style="text-align:center;">Flight controller/Processor</td><td style="text-align:center;">F427VIT6</td></tr><tr><td style="text-align:center;">Weight</td><td style="text-align:center;">~6g</td></tr><tr><td style="text-align:center;">Dimension</td><td style="text-align:center;">35x35mm</td></tr><tr><td style="text-align:center;">PWM Outputs</td><td style="text-align:center;">maximum 6</td></tr><tr><td style="text-align:center;">IMU</td><td style="text-align:center;">10DOF</td></tr><tr><td style="text-align:center;">IMU isolation</td><td style="text-align:center;">YES/Optional</td></tr><tr><td style="text-align:center;">Radio Receiver</td><td style="text-align:center;">S.BUS/PPM/DSM/DSM2/DSMX/SUMD</td></tr><tr><td style="text-align:center;">Telemetry</td><td style="text-align:center;">FrSky<sup>®</sup> D.Port, S.Port, Wifi, 3DR radio</td></tr><tr><td style="text-align:center;">On board TF card for flight data recording</td><td style="text-align:center;">YES</td></tr><tr><td style="text-align:center;">OneShot ESC Support</td><td style="text-align:center;">YES</td></tr><tr><td style="text-align:center;">Expansion Slots</td><td style="text-align:center;">2x7(pin)x2</td></tr><tr><td style="text-align:center;">On board Real time clock</td><td style="text-align:center;">YES</td></tr><tr><td style="text-align:center;">Connector</td><td style="text-align:center;">JST GH(compliance with DroneCode standard)</td></tr></tbody></table><h2 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h2><h3 id="pin-out-map" tabindex="-1">Pin-out Map <a class="header-anchor" href="#pin-out-map" aria-label="Permalink to &quot;Pin-out Map&quot;">​</a></h3><p><img src="'+e+'" alt="Mindracer pinout"></p><h3 id="how-to-build" tabindex="-1">How to Build <a class="header-anchor" href="#how-to-build" aria-label="Permalink to &quot;How to Build&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make airmind_mindpx-v2_default</span></span></code></pre></div><h3 id="companion-pc-connection" tabindex="-1">Companion PC Connection <a class="header-anchor" href="#companion-pc-connection" aria-label="Permalink to &quot;Companion PC Connection&quot;">​</a></h3><p>MindRacer has an attached Adapt IO board.</p><p><img src="'+a+'" alt="Attached Adapt IO board"></p><p>MindRacer has a built-in UART-to-USB converter. To connect a companion computer, stack MindRacer on an interface board, and connect the companion computer to the USB port on the interface board.</p><p>And the max BAUD rate is the same with px4 family, which is up to 921600.</p><h3 id="user-guide" tabindex="-1">User Guide <a class="header-anchor" href="#user-guide" aria-label="Permalink to &quot;User Guide&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">The user guide is <a href="http://mindpx.net/assets/accessories/mindracer_user_guide_v1.2.pdf" target="_blank" rel="noreferrer">here</a></p></div><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p>MindRacer is available at <a href="http://drupal.xitronet.com/?q=catalog" target="_blank" rel="noreferrer">AirMind Store</a>. You can also find MindRacer at Amazon<sup>®</sup> or eBay<sup>®</sup>.</p><h2 id="support" tabindex="-1">Support <a class="header-anchor" href="#support" aria-label="Permalink to &quot;Support&quot;">​</a></h2><p>Please visit <a href="http://www.mindpx.org" target="_blank" rel="noreferrer">http://www.mindpx.org</a> for more information. Or you can send email to <a href="mailto::support@mindpx.net" target="_blank" rel="noreferrer">support@mindpx.net</a> for any inquiries or help.</p>',29),d=[s];function c(p,h,u,m,f,g){return n(),i("div",null,d)}const y=r(l,[["render",c]]);export{x as __pageData,y as default};
