import{_ as t}from"./chunks/pixhack_v3_157_large_default.DxkjHHMr.js";import{_ as a,c as e,o as i,ab as l}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"Pixhack v3","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_controller/pixhack_v3.md","filePath":"zh/flight_controller/pixhack_v3.md"}'),r={name:"zh/flight_controller/pixhack_v3.md"},o=l('<h1 id="pixhack-v3" tabindex="-1">Pixhack v3 <a class="header-anchor" href="#pixhack-v3" aria-label="Permalink to &quot;Pixhack v3&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The CUAV <em>Pixhack V3</em> flight controller board is a flexible autopilot intended primarily for manufacturers of commercial systems.</p><p>The board is a variant of the SOLO Pixhawk<sup>®</sup> 2 (PH2) flight controller, which is in turn based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS, and is fully compatible with both PX4 or ArduPilot<sup>®</sup> (APM) firmware.</p><p><em>Pixhack V3</em> has significant improvements with respect to the original design, including better interface layout and the addition of vibration damping and a thermostat system.</p><p><img src="'+t+'" alt="Pixhack v3"></p><div class="info custom-block"><p class="custom-block-title">This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="快速预览" tabindex="-1">快速预览 <a class="header-anchor" href="#快速预览" aria-label="Permalink to &quot;快速预览&quot;">​</a></h2><ul><li>微处理器： <ul><li>STM32F427</li><li>STM32F100 (故障保护协处理器)</li></ul></li><li>传感器： <ul><li>加速度计 (3): LSM303D,MPU6000,MPU6000</li><li>陀螺仪 (3): L3GD20, MPU6000, MPU9250</li><li>指南针 (2): LS303D, MPU9250</li><li>气压计 (2): MS5611*2</li></ul></li><li>接口： <ul><li>MAVLink UART (2)</li><li>GPS UART (2)</li><li>DEBUG UART (1)</li><li>遥控信号输入(支持PPM, SBUS, DSM/DSM2)</li><li>RSSI输入: PWM或3.3ADC</li><li>I2C总线 (2)</li><li>CAN总线 (1)</li><li>ADC输入: 3.3V X1 , 6.6V X1</li><li>PWM输出: 8 PWM IO + 4 IO</li></ul></li><li>电源系统 <ul><li>Power口输入电压: 4.5 ~ 5.5 V</li><li>USB口输入电压：5.0 V +- 0.25v</li></ul></li><li>重量和尺寸: <ul><li>重量: 63g</li><li>宽度：68mm</li><li>厚度: 17mm</li><li>长度：44mm</li></ul></li><li>其它特性: <ul><li>工作温度: -20 ~ 60°C</li></ul></li></ul><h2 id="访问链接" tabindex="-1">访问链接 <a class="header-anchor" href="#访问链接" aria-label="Permalink to &quot;访问链接&quot;">​</a></h2><p>The board can be purchased from:</p><ul><li><a href="http://store.cuav.net/index.php?id_product=8&amp;id_product_attribute=0&amp;rewrite=pixhack-v3-autopilot&amp;controller=product&amp;id_lang=3" target="_blank" rel="noreferrer">store.cuav.net</a></li><li><a href="https://leixun.aliexpress.com/store" target="_blank" rel="noreferrer">leixun.aliexpress.com/store</a></li></ul><h2 id="编译固件" tabindex="-1">编译固件 <a class="header-anchor" href="#编译固件" aria-label="Permalink to &quot;编译固件&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="引脚和原理图" tabindex="-1">引脚和原理图 <a class="header-anchor" href="#引脚和原理图" aria-label="Permalink to &quot;引脚和原理图&quot;">​</a></h2><ul><li><a href="http://doc.cuav.net/flight-controller/pixhack/en/pixhack-v3.html" target="_blank" rel="noreferrer">Documentation/wiring guides</a></li></ul><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table><thead><tr><th>UART</th><th>设备</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',20),n=[o];function s(d,h,c,p,u,m){return i(),e("div",null,n)}const g=a(r,[["render",s]]);export{b as __pageData,g as default};
