import{_ as e}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as t}from"./chunks/remote_control.ss1psCxn.js";import{_ as a}from"./chunks/acrobatic_fw.CXa-EOaK.js";import{_ as r,c as o,o as i,ab as d}from"./chunks/framework.CUflZczI.js";const W=JSON.parse('{"title":"Acro Mode (Fixed-Wing)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_modes_fw/acro.md","filePath":"zh/flight_modes_fw/acro.md"}'),c={name:"zh/flight_modes_fw/acro.md"},_=d('<h1 id="acro-mode-fixed-wing" tabindex="-1">Acro Mode (Fixed-Wing) <a class="header-anchor" href="#acro-mode-fixed-wing" aria-label="Permalink to &quot;Acro Mode (Fixed-Wing)&quot;">​</a></h1><p><img src="'+e+'" title="很难飞" width="30px"> <img src="'+t+'" title="需要手动或遥控控制" width="30px"> </p><p><em>Acro mode</em> is the manual mode for performing acrobatic maneuvers e.g. rolls, flips, stalls and acrobatic figures.</p><p>The roll, pitch and yaw sticks control the rate of angular rotation around the respective axes and throttle is passed directly to control allocation. 当操纵杆居中时，飞机将停止旋转，但保持其当前朝向（在其侧面，倒置或任何其他方向）并根据当前动量移动。 When sticks are centered the vehicle will stop rotating, but remain in its current orientation (on its side, inverted, or whatever) and moving according to its current momentum.</p><p><img src="'+a+'" alt="固定翼手动特技飞行"></p><h2 id="技术描述" tabindex="-1">技术描述 <a class="header-anchor" href="#技术描述" aria-label="Permalink to &quot;技术描述&quot;">​</a></h2><p>用于执行杂技动作的遥控模式，例如滚动、翻转、摊位和杂技图形。</p><p>RPY摇杆输入被转换为角速度命令，通过自动驾驶仪稳定。 Throttle is passed directly to control allocation.</p><h2 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h2><table><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td><a id="FW_ACRO_X_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_X_MAX">FW_ACRO_X_MAX</a></td><td>机体轴x轴最大速率（用户在acro模式下施加滚转轴满杆操纵时，控制器试图达到的机体轴x轴速率） 默认：90度 Default: 90 degrees.</td></tr><tr><td><a id="FW_ACRO_Y_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_Y_MAX">FW_ACRO_Y_MAX</a></td><td>Acro body y max rate (the body y rate the controller is trying to achieve if the user applies full pitch stick input in acro mode). Default: 90 degrees.</td></tr><tr><td><a id="FW_ACRO_Z_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_Z_MAX">FW_ACRO_Z_MAX</a></td><td>机体轴z轴最大速率（用户在acro模式下施加偏航轴满杆操纵时，控制器试图达到的机体轴z轴速率） 默认：45度 Default: 45 degrees.</td></tr></tbody></table>',10),n=[_];function s(l,h,m,p,f,g){return i(),o("div",null,n)}const v=r(c,[["render",s]]);export{W as __pageData,v as default};
