import{_ as t}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as a}from"./chunks/remote_control.ss1psCxn.js";import{_ as e,c as o,o as r,ab as i}from"./chunks/framework.CUflZczI.js";const g=JSON.parse('{"title":"Manual Mode (Fixed-wing)","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_modes_fw/manual.md","filePath":"zh/flight_modes_fw/manual.md"}'),d={name:"zh/flight_modes_fw/manual.md"},_=i('<h1 id="manual-mode-fixed-wing" tabindex="-1">Manual Mode (Fixed-wing) <a class="header-anchor" href="#manual-mode-fixed-wing" aria-label="Permalink to &quot;Manual Mode (Fixed-wing)&quot;">​</a></h1><p><img src="'+t+'" title="很难飞" width="30px"> <img src="'+a+'" title="需要手动或遥控控制" width="30px"> </p><p><em>Manual mode</em> sends manual stick input directly to control allocation for fully manual control.</p><p>This is the hardest mode to fly, because nothing is stabilised. 不同于<a href="./../flight_modes/acro_fw.html">特技模式</a>，如果 RP （俯仰和横滚） 摇杆居中，无人机不会自动停止绕轴转；飞手实际上必须移动摇杆向另一个方向施力。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This is the only mode that overrides the FMU (commands are sent via the safety coprocessor). 该模式提供了一个安全机制，允许在 FMU 固件故障时通过遥控器完全控制油门，升降舵，副翼和方向舵。</p></div><h2 id="技术描述" tabindex="-1">技术描述 <a class="header-anchor" href="#技术描述" aria-label="Permalink to &quot;技术描述&quot;">​</a></h2><p>RC mode where stick input is sent directly to control allocation (for &quot;fully&quot; manual control).</p><p>这是唯一忽略 FMU（命令通过安全协处理器发送） 的飞行模式。 该模式提供了一个安全机制，允许在 FMU 固件故障时通过遥控器完全控制油门，升降舵，副翼和方向舵。</p><h2 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h2><table><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td><a id="FW_MAN_P_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_SC">FW_MAN_P_SC</a></td><td>手动俯仰控制比例。 在完全手动模式下应用于所需俯仰舵机指令的比例因子。 此参数允许调整控制舵偏的偏转。 默认：1.0</td></tr><tr><td><a id="FW_MAN_R_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_SC">FW_MAN_R_SC</a></td><td>手动滚转控制比例。 在完全手动模式下应用于所需横滚舵机指令的比例因子。 此参数允许调整控制舵偏的偏转。 默认：1.0</td></tr><tr><td><a id="FW_MAN_Y_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_Y_SC">FW_MAN_Y_SC</a></td><td>手动偏航控制比例。 在完全手动模式下应用于所需偏航舵机指令的比例因子。 此参数允许调整控制舵偏的偏转。 默认：1.0</td></tr></tbody></table>',10),n=[_];function l(s,c,h,m,f,p){return r(),o("div",null,n)}const b=e(d,[["render",l]]);export{g as __pageData,b as default};
