import{_ as a}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as t,c as e,o as _,ab as r}from"./chunks/framework.CUflZczI.js";const D=JSON.parse('{"title":"着陆模式（多旋翼）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/flight_modes_mc/land.md","filePath":"zh/flight_modes_mc/land.md"}'),l={name:"zh/flight_modes_mc/land.md"},i=r('<h1 id="着陆模式-多旋翼" tabindex="-1">着陆模式（多旋翼） <a class="header-anchor" href="#着陆模式-多旋翼" aria-label="Permalink to &quot;着陆模式（多旋翼）&quot;">​</a></h1><img src="'+a+'" title="需要定位估计（例如GPS）" width="30px"><p>_着陆_飞行模式使飞机降落在指定点。 这辆车在降落后不久将被解除武装(默认)。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>该模式是自动的 - 不 <em>需要</em> 用户干预即可控制无人机。</li><li>模式需要至少有效的本地位置估计(不需要全球位置)。 <ul><li>没有有效的本地位置，飞行器不能切换到此模式。</li><li>飞行器如果失去位置估计，就会失效。</li></ul></li><li>模式阻止武装(切换到此模式时车辆必须备用)。</li><li>遥控器开关可以用于更改任何无人机的飞行模式。</li><li>在多旋翼中移动遥控器摇杆（或 VTOL 在多旋翼模式下）<a href="#COM_RC_OVERRIDE">默认情况下</a>会将无人机切换到<a href="./../flight_modes_mc/position.html">位置模式</a>，除非是处理电池失效保护。</li><li>模式可以使用 <a href="https://mavlink.io/en/messages/common.html#MAV_CMD_NAV_LAND" target="_blank" rel="noreferrer">MAV_CMD_NAV_LAND</a> MAVLink 命令或明确切换到着陆模式。</li></ul></div><h2 id="技术摘要" tabindex="-1">技术摘要 <a class="header-anchor" href="#技术摘要" aria-label="Permalink to &quot;技术摘要&quot;">​</a></h2><p>无人机将降落在模式所指定的位置。 无人机以 <a href="#MPC_LAND_SPEED">MPC_LAND_SPEED</a> 指定的速度下降，降落后会上锁（<a href="#COM_DISARM_LAND">默认</a>）。</p><p>遥控器摇杆移动会将无人机切换到 <a href="./../flight_modes_mc/position.html">位置模式</a> （<a href="#COM_RC_OVERRIDE">默认</a>）。</p><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h3><p>着陆模式行为可以使用下面的参数进行配置。</p><table><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td><a id="MPC_LAND_SPEED"></a><a href="./../advanced_config/parameter_reference.html#MPC_LAND_SPEED">MPC_LAND_SPEED</a></td><td>着陆过程中的下降速率。 鉴于地面情况未知，这个值应该设得相当小。</td></tr><tr><td><a id="COM_DISARM_LAND"></a><a href="./../advanced_config/parameter_reference.html#COM_DISARM_LAND">COM_DISARM_LAND</a></td><td>降落后自动上锁的超时时间，以秒为单位。 如果设定为 -1，无人机将不会在着陆时上锁。</td></tr><tr><td><a id="COM_RC_OVERRIDE"></a><a href="./../advanced_config/parameter_reference.html#COM_RC_OVERRIDE">COM_RC_OVERRIDE</a></td><td>控制多旋翼（或者多旋翼模式下的 VOTL）的摇杆移动量来切换到 <a href="./../flight_modes_mc/position.html">位置模式</a>。 可以分别为自动模式和 offboard 模式启用此功能，默认情况下在自动模式下启用此功能。</td></tr><tr><td><a id="COM_RC_STICK_OV"></a><a href="./../advanced_config/parameter_reference.html#COM_RC_STICK_OV">COM_RC_STICK_OV</a></td><td>导致发射机切换到 <a href="./../flight_modes_mc/position.html">位置模式</a> 的摇杆移动量（如果 <a href="#COM_RC_OVERRIDE">COM_RC_OVERRIDE</a> 已启用）。</td></tr></tbody></table><h2 id="另请参阅" tabindex="-1">另请参阅 <a class="header-anchor" href="#另请参阅" aria-label="Permalink to &quot;另请参阅&quot;">​</a></h2><ul><li><a href="./../flight_modes_fw/land.html">着陆模式（固定翼）</a></li><li><a href="./../flight_modes_vtol/land.html">着陆模式（垂直起降）</a></li></ul>',12),d=[i];function o(h,n,m,s,c,f){return _(),e("div",null,d)}const R=t(l,[["render",o]]);export{D as __pageData,R as default};
