import{_ as t,a}from"./chunks/locosys_hawk_a1_leds.Br0RVX18.js";import{_ as e}from"./chunks/locosys_gps_cable.i1V998rT.js";import{_ as r,c as o,o as i,ab as n}from"./chunks/framework.CUflZczI.js";const G=JSON.parse('{"title":"LOCOSYS Hawk A1 GPS/GNSS","description":"","frontmatter":{},"headers":[],"relativePath":"zh/gps_compass/gps_locosys_hawk_a1.md","filePath":"zh/gps_compass/gps_locosys_hawk_a1.md"}'),d={name:"zh/gps_compass/gps_locosys_hawk_a1.md"},s=n('<h1 id="locosys-hawk-a1-gps-gnss" tabindex="-1">LOCOSYS Hawk A1 GPS/GNSS <a class="header-anchor" href="#locosys-hawk-a1-gps-gnss" aria-label="Permalink to &quot;LOCOSYS Hawk A1 GPS/GNSS&quot;">​</a></h1><p>The <a href="https://www.locosystech.com/en/product/hawk-a1-LU23031-V2.html" target="_blank" rel="noreferrer">LOCOSYS HAWK A1 GPS/GNSS receiver</a> is a dual frequency multi-constellation GNSS/GPS receiver compatible with PX4.</p><p>Main features include:</p><ul><li>Concurrent reception of L1 and L5 band signals</li><li>Supports GPS, GLONASS,BEIDOU, GALILEO, QZSS</li><li>Capable of SBAS (WAAS, EGNOS, MSAS, GAGAN)</li><li>Support 135-channel GNSS</li><li>Fast TTFF at low signal level</li><li>Free hybrid ephemeris prediction to achieve faster cold start</li><li>Default 5Hz, up to 10 Hz update rate (SBAS at 5Hz update rate only)</li><li>Built-in super capacitor to reserve system data for rapid satellite acquisition</li><li>Three LED indicator for Power, PPS and Data transmit</li></ul><p><img src="'+t+'" alt="Hawk A1"></p><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><ul><li><a href="https://www.locosystech.com/en/product/hawk-a1-LU23031-V2.html" target="_blank" rel="noreferrer">LOCOSYS</a> (Taiwan)</li></ul><h2 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h2><p>You can use the Hawk A1 as either main (primary) or secondary GPS system. The PX4 parameters should be set as below for each case.</p><h3 id="main-gnss" tabindex="-1">Main GNSS <a class="header-anchor" href="#main-gnss" aria-label="Permalink to &quot;Main GNSS&quot;">​</a></h3><p>To use the Hawk A1 your main GPS device:</p><table><thead><tr><th>Parameter</th><th>Value</th><th>Description</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_1_CONFIG">GPS_1_CONFIG</a></td><td>102 (Telem 2 or another available serial port)</td><td>Configure main GPS port</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_1_PROTOCOL">GPS_1_PROTOCOL</a></td><td>1 (u-blox)</td><td>Configure GPS protocol</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#SER_TEL2_BAUD">SER_TEL2_BAUD</a></td><td>230400</td><td>Configure the serial port baudrate (here the GPS is connected to <code>TELEM2</code> for instance)</td></tr></tbody></table><h3 id="secondary-gnss" tabindex="-1">Secondary GNSS <a class="header-anchor" href="#secondary-gnss" aria-label="Permalink to &quot;Secondary GNSS&quot;">​</a></h3><p>To use the Hawk A1 as an auxiliary GPS device (in addition to the main GPS):</p><table><thead><tr><th>Parameter</th><th>Value</th><th>Description</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_2_CONFIG">GPS_2_CONFIG</a></td><td>102 (Telem 2 or another available serial port)</td><td>Configure main GPS port</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_2_PROTOCOL">GPS_2_PROTOCOL</a></td><td>1 (u-blox)</td><td>Configure GPS protocol</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#SER_TEL2_BAUD">SER_TEL2_BAUD</a></td><td>230400</td><td>Configure the serial port baudrate (here the GPS is connected to TELEM2 for instance)</td></tr></tbody></table><h2 id="wiring-and-connections" tabindex="-1">Wiring and Connections <a class="header-anchor" href="#wiring-and-connections" aria-label="Permalink to &quot;Wiring and Connections&quot;">​</a></h2><p>The Locosys GPS comes with an 6-pin JST-GH Pixhawk-standard connector that can be inserted directly into either the GPS1 UART port (or GPS2 UART ports from Pixhawk FMUv5).</p><p><img src="'+e+'" alt="GPS cable"></p><h3 id="pinout" tabindex="-1">Pinout <a class="header-anchor" href="#pinout" aria-label="Permalink to &quot;Pinout&quot;">​</a></h3><p>The LOCOSYS GPS pinout is provided below. This can be used to help modify the connector for other autopilot boards.</p><table><thead><tr><th>pin</th><th>Locosys GPS</th><th>pin</th><th>Pixhawk GPS 2</th></tr></thead><tbody><tr><td>1</td><td>VCC_5V</td><td>1</td><td>VCC</td></tr><tr><td>2</td><td>GPS_RX</td><td>2</td><td>GPS_TX</td></tr><tr><td>3</td><td>GPS_TX</td><td>3</td><td>GPS_RX</td></tr><tr><td>4</td><td>NC</td><td>4</td><td>SDA</td></tr><tr><td>5</td><td>NC</td><td>5</td><td>SCL</td></tr><tr><td>6</td><td>GND</td><td>6</td><td>GND</td></tr></tbody></table><h2 id="status-leds" tabindex="-1">Status LEDs <a class="header-anchor" href="#status-leds" aria-label="Permalink to &quot;Status LEDs&quot;">​</a></h2><table><thead><tr><th>Color</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td>Green</td><td>TX Indicator</td><td>GNSS Data transmission</td></tr><tr><td>Red</td><td>Power Indicator</td><td>Power</td></tr><tr><td>Blue</td><td>PPS</td><td>Precise Positioning Service active</td></tr></tbody></table><p><img src="'+a+'" alt="Hawk A1 LEDs"></p><h2 id="specifications" tabindex="-1">Specifications <a class="header-anchor" href="#specifications" aria-label="Permalink to &quot;Specifications&quot;">​</a></h2><ul><li><strong>Receiver Type:</strong> 135-channel LOCOSYS MC-1612-V2b engine, GPS/QZSS L1 C/A, L5C, GLONASS L1OF, BeiDou B1I, B2a Galileo:E1, E5a SBAS L1 C/A: WAAS, EGNOS, MSAS, GAGAN</li><li><strong>Navigation Update Rate:</strong> Max: 5Hz default Max: 10 Hz</li><li><strong>Positioning Accuracy:</strong> 3D Fix</li><li><strong>Time to first fix:</strong><ul><li><strong>Cold start:</strong> 28s</li><li><strong>Aided start:</strong> EASY</li></ul></li><li><strong>Sensitivity:</strong><ul><li><strong>Tracking &amp; Navigation:</strong> -165 dBm</li></ul></li><li><strong>Assisted GNSS:</strong> EASY DGPS</li><li><strong>Oscillator:</strong> 26Mhz TCXO</li><li><strong>RTC crystal:</strong> 32.768KHz</li><li><strong>Available Antennas:</strong> L1+L5 multi frequency antenna</li><li><strong>Signal Integrity:</strong> L1+L5 GPS GLONASS GALILEO BEIDOU QZSS SBAS</li><li><strong>Protocols &amp; Interfaces:</strong><ul><li><strong>UART/I2C:</strong> JST_GH Main interface, Switch internally.</li></ul></li></ul><h2 id="further-info" tabindex="-1">Further info <a class="header-anchor" href="#further-info" aria-label="Permalink to &quot;Further info&quot;">​</a></h2><ul><li><a href="https://www.locosystech.com/Templates/att/LU23031-V2%20datasheet_v0.2.pdf?lng=en" target="_blank" rel="noreferrer">LOCOSYS GPS User Manual</a></li></ul>',28),l=[s];function h(c,S,p,u,_,f){return i(),o("div",null,l)}const b=r(d,[["render",h]]);export{G as __pageData,b as default};
