import{_ as e}from"./chunks/leddar_one.COew240k.js";import{_ as a,c as t,o as r,ab as d}from"./chunks/framework.CUflZczI.js";const b=JSON.parse('{"title":"LeddarOne Lidar","description":"","frontmatter":{},"headers":[],"relativePath":"zh/sensor/leddar_one.md","filePath":"zh/sensor/leddar_one.md"}'),n={name:"zh/sensor/leddar_one.md"},o=d('<h1 id="leddarone-lidar" tabindex="-1">LeddarOne Lidar <a class="header-anchor" href="#leddarone-lidar" aria-label="Permalink to &quot;LeddarOne Lidar&quot;">​</a></h1><p><a href="https://leddartech.com/solutions/leddarone/" target="_blank" rel="noreferrer">LeddarOne</a> is small Lidar module with a narrow, yet diffuse beam that offers excellent overall detection range and performance, in a robust, reliable, cost-effective package. It has a sensing range from 1cm to 40m and needs to be connected to a UART/serial bus.</p><img src="'+e+'" alt="LeddarOne Lidar rangefinder" width="200px"><h2 id="硬件安装" tabindex="-1">硬件安装 <a class="header-anchor" href="#硬件安装" aria-label="Permalink to &quot;硬件安装&quot;">​</a></h2><p>LeddarOne can be connected to any unused <em>serial port</em> (UART), e.g.: TELEM2, TELEM3, GPS2 etc.</p><p>Build a cable following your board and pinout and LeddarOne pinout (shown below). You only will need to connect 5V, TX, RX and GND pins.</p><table><thead><tr><th>针脚</th><th>LeddarOne</th></tr></thead><tbody><tr><td>1</td><td>GND</td></tr><tr><td>2</td><td>-</td></tr><tr><td>3</td><td>VCC</td></tr><tr><td>4</td><td>RX</td></tr><tr><td>5</td><td>TX</td></tr><tr><td>6</td><td>-</td></tr></tbody></table><h2 id="parameter-setup" tabindex="-1">Parameter Setup <a class="header-anchor" href="#parameter-setup" aria-label="Permalink to &quot;Parameter Setup&quot;">​</a></h2><p><a href="./../peripherals/serial_configuration.html">Configure the serial port</a> on which the lidar will run using <a href="./../advanced_config/parameter_reference.html#SENS_LEDDAR1_CFG">SENS_LEDDAR1_CFG</a>. 无需设置端口的波特率, 因为这是由驱动程序配置的。</p><div class="info custom-block"><p class="custom-block-title">If the configuration parameter is not available in <em>QGroundControl</em> then you may need to <a href="./../peripherals/serial_configuration.html#parameter_not_in_firmware">add the driver to the firmware</a>:</p><div class="language-plain vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">plain</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>CONFIG_DRIVERS_DISTANCE_SENSOR_LEDDAR_ONE=y</span></span></code></pre></div></div><h2 id="更多信息" tabindex="-1">更多信息 <a class="header-anchor" href="#更多信息" aria-label="Permalink to &quot;更多信息&quot;">​</a></h2><ul><li><a href="https://leddartech.com/app/uploads/dlm_uploads/2021/04/Spec-Sheet_LeddarOne_V10.0_EN-1.pdf" target="_blank" rel="noreferrer">LeddarOne Spec sheet</a></li></ul>',12),i=[o];function l(s,c,h,p,_,m){return r(),t("div",null,i)}const g=a(n,[["render",l]]);export{b as __pageData,g as default};
