import{_ as e}from"./chunks/tfmini_hero.D5WcvH71.js";import{_ as i,c as a,o as r,ab as t}from"./chunks/framework.CUflZczI.js";const T=JSON.parse('{"title":"Benewake TFmini LiDAR","description":"","frontmatter":{},"headers":[],"relativePath":"zh/sensor/tfmini.md","filePath":"zh/sensor/tfmini.md"}'),n={name:"zh/sensor/tfmini.md"},o=t('<h1 id="benewake-tfmini-lidar" tabindex="-1">Benewake TFmini LiDAR <a class="header-anchor" href="#benewake-tfmini-lidar" aria-label="Permalink to &quot;Benewake TFmini LiDAR&quot;">​</a></h1><p>The <em>Benewake TFmini LiDAR</em> is a tiny, low cost, and low power LIDAR with 12m range.</p><p>PX4 supports all three variants in the series: TFmini-s, TFmini-i, TFmini Plus. These must be connected to a UART/serial bus.</p><p><img src="'+e+'" alt="TFmini LiDAR"></p><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><ul><li><a href="https://en.benewake.com/TFminiS/index_proid_325.html" target="_blank" rel="noreferrer">TFmini-s</a></li><li><a href="https://en.benewake.com/TFminii/index_proid_324.html" target="_blank" rel="noreferrer">TFmini-i</a> (industrial)</li><li><a href="https://en.benewake.com/TFminiPlus/index_proid_323.html" target="_blank" rel="noreferrer">TFmini Plus</a></li></ul><h2 id="硬件安装" tabindex="-1">硬件安装 <a class="header-anchor" href="#硬件安装" aria-label="Permalink to &quot;硬件安装&quot;">​</a></h2><p>TFmini can be connected to any unused <em>serial port</em> (UART), such as: <code>TELEM2</code>, <code>TELEM3</code>, <code>GPS2</code> etc.</p><h2 id="parameter-setup" tabindex="-1">Parameter Setup <a class="header-anchor" href="#parameter-setup" aria-label="Permalink to &quot;Parameter Setup&quot;">​</a></h2><p><a href="./../peripherals/serial_configuration.html">Configure the serial port</a> on which the lidar will run using <a href="./../advanced_config/parameter_reference.html#SENS_TFMINI_CFG">SENS_TFMINI_CFG</a>. There is no need to set the baud rate (this is hard coded in the sensor driver as only one rate is supported).</p><div class="info custom-block"><p class="custom-block-title">If the configuration parameter is not available in <em>QGroundControl</em> then the <a href="./../modules/modules_driver_distance_sensor.html#tfmini">tfmini</a> driver may not be in firmware. For information on how to add it see:</p><ul><li><a href="./../peripherals/serial_configuration.html#parameter_not_in_firmware">Serial port Configuration: Configuration Parameter Missing from QGroundControl</a></li><li><a href="./../hardware/porting_guide_config.html#px4-menuconfig-setup">PX4 Board Configuration (Kconfig)</a>.</li></ul></div>',11),s=[o];function l(m,h,d,c,p,u){return r(),a("div",null,s)}const b=i(n,[["render",l]]);export{T as __pageData,b as default};
