import{_ as a,c as e,o as t,ab as p}from"./chunks/framework.CUflZczI.js";const m=JSON.parse('{"title":"测试 MC_04 -故障安全测试","description":"","frontmatter":{},"headers":[],"relativePath":"zh/test_cards/mc_04_failsafe_testing.md","filePath":"zh/test_cards/mc_04_failsafe_testing.md"}'),r={name:"zh/test_cards/mc_04_failsafe_testing.md"},s=p('<h1 id="测试-mc-04-故障安全测试" tabindex="-1">测试 MC_04 -故障安全测试 <a class="header-anchor" href="#测试-mc-04-故障安全测试" aria-label="Permalink to &quot;测试 MC_04 -故障安全测试&quot;">​</a></h1><p>❏ 验证 RC Loss 动作是否返回陆地</p><p>❏ 验证数据链路丢失操作返回陆地，超时是10秒</p><p>❏ 验证电池是否安全</p><p>    ❏ 行动是返回土地</p><p>    ❏ 电池警告等级为 25％</p><p>    ❏ 电池故障安全等级为 20％</p><p>    ❏ 电池紧急等级为 15％</p><p>❏ 在高度模式下起飞</p><p>❏ 离开原位至少 20 米</p><p>❏ RC 损失</p><p>    ❏ 关闭 RC 并检查设备返回原位，等待下降并打开 RC 并接管。</p><h2 id="数据链路丢失" tabindex="-1">数据链路丢失 <a class="header-anchor" href="#数据链路丢失" aria-label="Permalink to &quot;数据链路丢失&quot;">​</a></h2><p>❏ 断开遥测，车辆应在 10 秒后返回原位，等待下降并重新连接遥测无线电</p><h2 id="切换到高度模式" tabindex="-1">切换到高度模式 <a class="header-anchor" href="#切换到高度模式" aria-label="Permalink to &quot;切换到高度模式&quot;">​</a></h2><p>❏ 确保横滚，俯仰和偏航杆像稳定模式一样响应</p><p>❏ 节流阀应控制高度，当操纵杆居中时，必须保持高度</p><h2 id="切换到位置模式" tabindex="-1">切换到位置模式 <a class="header-anchor" href="#切换到位置模式" aria-label="Permalink to &quot;切换到位置模式&quot;">​</a></h2><p>❏ 当棒居中时，它必须保持位置</p><p>❏移动横滚，俯仰和偏航，并根据输入检查无人机是否在移动</p><p>❏ 再次将木棒对中并检查无人机是否保持位置</p><h2 id="电量故障保护触发器" tabindex="-1">电量故障保护触发器 <a class="header-anchor" href="#电量故障保护触发器" aria-label="Permalink to &quot;电量故障保护触发器&quot;">​</a></h2><p>❏ 确认 QGC 收到警告信息</p><p>❏ 确认无人机返回降落状态</p><p>❏ 确认车辆着陆。</p>',25),_=[s];function o(i,c,n,h,l,d){return t(),e("div",null,_)}const u=a(r,[["render",o]]);export{m as __pageData,u as default};
