import{_ as a,c as e,o as t,ab as i}from"./chunks/framework.CUflZczI.js";const u=JSON.parse('{"title":"测试 MC_05-室内飞行（手动模式）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/test_cards/mc_05_indoor_flight_manual_modes.md","filePath":"zh/test_cards/mc_05_indoor_flight_manual_modes.md"}'),r={name:"zh/test_cards/mc_05_indoor_flight_manual_modes.md"},o=i('<h1 id="测试-mc-05-室内飞行-手动模式" tabindex="-1">测试 MC_05-室内飞行（手动模式） <a class="header-anchor" href="#测试-mc-05-室内飞行-手动模式" aria-label="Permalink to &quot;测试 MC_05-室内飞行（手动模式）&quot;">​</a></h1><h2 id="何时使用此测试卡" tabindex="-1">何时使用此测试卡 <a class="header-anchor" href="#何时使用此测试卡" aria-label="Permalink to &quot;何时使用此测试卡&quot;">​</a></h2><ul><li>新建初次飞行</li><li>当需要在受限区域复制问题时</li><li>可能存在稳定性问题的实验性构建</li><li>测试已更换和/或修改的硬件</li></ul><h2 id="解锁并起飞" tabindex="-1">解锁并起飞 <a class="header-anchor" href="#解锁并起飞" aria-label="Permalink to &quot;解锁并起飞&quot;">​</a></h2><p>❏ 设置飞行模式以稳定和布防</p><p>❏ 升高油门起飞</p><h2 id="飞行" tabindex="-1">飞行 <a class="header-anchor" href="#飞行" aria-label="Permalink to &quot;飞行&quot;">​</a></h2><p>❏ 自稳</p><p>    ❏ 俯仰/滚转/偏航响应 1：1</p><p>    ❏ 节气门响应 1：1</p><p>❏ 高度</p><p>    ❏ 垂直位置应以棒为中心保持当前值</p><p>    ❏ 俯仰/滚转/偏航响应 1：1</p><p>    ❏ 将节气门响应设置为爬升/下降速率</p><h2 id="着陆" tabindex="-1">着陆 <a class="header-anchor" href="#着陆" aria-label="Permalink to &quot;着陆&quot;">​</a></h2><p>❏ 以稳定或高度模式着陆，油门低于 40％</p><p>❏ Upon touching ground, copter should disarm automatically within 2 seconds (disarm time set by parameter: <a href="./../advanced_config/parameter_reference.html#COM_DISARM_LAND">COM_DISARM_LAND</a>)</p><h2 id="期待的结果" tabindex="-1">期待的结果 <a class="header-anchor" href="#期待的结果" aria-label="Permalink to &quot;期待的结果&quot;">​</a></h2><ul><li>当油门升高时，起飞应该是平稳的</li><li>在上述任何飞行模式中都不应出现振荡</li><li>着陆时，直升机不应在地面上反弹</li></ul>',19),l=[o];function _(n,d,s,h,c,p){return t(),e("div",null,l)}const f=a(r,[["render",_]]);export{u as __pageData,f as default};
